import { View, Text, FlatList } from "react-native";
import React from "react";
import ProductDisplayBtn from "components/ProductDisplayBtn";
import { userStoreState } from "state/state";

const MenuScreenInnerBlock = ({ section, height }) => {
  const catalog = userStoreState.use();

  if (catalog.products?.length > 0) {
    // return catalog.products.map((product, index) => {
    //   const isVisible = section
    //     ? product.catagory === section || product.category === section
    //     : product.catagory === catalog.categories[0] ||
    //       product.category === catalog.categories[0];

    //   return (
    //     <>
    //       {isVisible && (
    //         <ProductDisplayBtn
    //           product={product}
    //           productIndex={index}
    //           key={index}
    //         />
    //       )}
    //     </>
    //   );
    // });

    return (
      <FlatList
        style={{ width: "100%", height: "100%" }}
        contentContainerStyle={{
          flexDirection: "row",
          flexWrap: "wrap",
          justifyContent: "spa",
          paddingLeft: 50,
          paddingRight: 50,
        }}
        data={catalog.products}
        renderItem={({ item, index }) => {
          const isVisible = section
            ? item.catagory === section || item.category === section
            : item.catagory === catalog.categories[0] ||
              item.category === catalog.categories[0];
          return (
            <>
              {isVisible && (
                <ProductDisplayBtn
                  product={item}
                  productIndex={index}
                  key={index}
                />
              )}
            </>
          );
        }}
        keyExtractor={(item) => item.id}
        getItemLayout={(data, index) => ({
          length: 180,
          offset: 180 * index,
          index,
        })}
      />
    );
  } else {
    return (
      <View
        style={{
          width: "100%",
          height: height * 0.8,
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Text
          style={{
            fontFamily: "archivo-500",
            color: "rgba(74,74,74,1)",
            fontSize: 20,
          }}
        >
          This category has no products...
        </Text>
      </View>
    );
  }
};

export default MenuScreenInnerBlock;
