import { SafeAreaView, StyleSheet, Text, TextInput, View } from "react-native";
import React, { useState } from "react";
import { GooglePlacesAutocomplete } from "react-native-google-places-autocomplete";

const GOOGLE_API_KEY = "AIzaSyB5l-wEA7ueMuvdHqVZK8ZD7-1yMLV076M";

const App = () => {
  const [listOfAddresses, setlistOfAddresses] = useState([]);

  const GooglePlacesInput = ({ placeholder }) => {
    return (
      <GooglePlacesAutocomplete
        placeholder={placeholder}
        fetchDetails={true}
        onPress={(data, details) => {
          // 'details' is provided when fetchDetails = true
          console.log(data, details);
        }}
        query={{
          key: GOOGLE_API_KEY,
          language: "en",
        }}
        textInputProps={{
          style: {
            height: 40,
            width: 500,
            padding: 10,
            fontSize: 15,
            borderWidth: 2,
            borderRadius: 6,
            margin: 10,
          },
        }}
        requestUrl={{
          useOnPlatform: "web", // or "all"
          url: "https://cors-anywhere.herokuapp.com/https://maps.googleapis.com/maps/api", // or any proxy server that hits https://maps.googleapis.com/maps/api
          // headers: {
          //   Authorization: `an auth token`, // if required for your proxy
          // },
        }}
      />
    );
  };

  const DistanceMatrix = async () => {
    await fetch(
      `https://maps.googleapis.com/maps/api/distancematrix/json?origins=${
        myCurrentPosition.location.lat
      },${myCurrentPosition.location.lng}&destinations=${
        documentSnapshot.data().driverLat
      },${documentSnapshot.data().driverLng}&key=${GOOGLE_MAPS_APIKEY}`
    )
      .then((response) => response.json())
      .then((responseJson) => {});
  };

  return (
    <SafeAreaView style={{ flex: 1 }}>
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <Text>Get The Best Route List</Text>
        {listOfAddresses.length < 1 ? (
          <GooglePlacesInput placeholder="Start Address" />
        ) : (
          <GooglePlacesInput placeholder="Enter Next Stop" />
        )}
      </View>
    </SafeAreaView>
  );
};

export default App;

const styles = StyleSheet.create({});
