import React, { useState } from "react";

const GeneralSwitch = ({ isActive, setIsActive, customToggleFunctionOn, customToggleFunctionOff }) => {
  // Function to toggle switch state
    const toggleSwitch = () => {
        if (setIsActive) {
            setIsActive(!isActive)
        }

        if (isActive) {
            if(cust)
            customToggleFunctionOff()
        } else {
            customToggleFunctionOn()
        }
    };

  // Styles
  const switchStyles = {
    cursor: "pointer",
    width: "60px",
    height: "30px",
    borderRadius: "15px",
    backgroundColor: isActive ? "#1D294E" : "#ccc",
    position: "relative",
    transition: "background-color 0.3s",
  };

  const knobStyles = {
    position: "absolute",
    top: "2px",
    left: isActive ? "32px" : "2px", // Move knob based on isActive
    width: "26px",
    height: "26px",
    borderRadius: "50%",
    backgroundColor: "#fff",
    transition: "left 0.3s",
  };

  return (
    <div style={switchStyles} onClick={toggleSwitch}>
      <div style={knobStyles}></div>
    </div>
  );
};

export default GeneralSwitch;
