import React, { Component, useState } from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import OptionSelectionItem from "./OptionSelectionItem";
import { Entypo, MaterialCommunityIcons } from "@expo/vector-icons";

function OptionsItem({
  item,
  index,
  setnewProduct,
  newProduct,
  newProductOptions,
  setnewProductOptions,
  indexOn,
  setindexOn,
  style,
}) {
  const [e, sete] = useState(structuredClone(item));

  return (
    <>
      <View
        style={[
          styles.container,
          style,
          indexOn === index && { paddingBottom: 20 },
        ]}
      >
        <TouchableOpacity
          style={[
            styles.closedOptionContainer,
            index === indexOn && { borderBottomWidth: 1 },
          ]}
          onPress={() =>
            indexOn !== index ? setindexOn(index) : setindexOn(null)
          }
          activeOpacity={0.8}
        >
          <Text style={styles.optionNameLbl}>
            {item.label ? item.label : "New Option"}
          </Text>
          <View style={styles.btnsRow}>
            <TouchableOpacity
              style={styles.moveDownBtn}
              onPress={() => {
                if (
                  newProductOptions.length > 1 &&
                  index !== newProductOptions.length - 1
                ) {
                  setnewProductOptions((prev) => {
                    const clone = structuredClone(prev);
                    const f = clone.splice(index, 1)[0];
                    clone.splice(index + 1, 0, f);
                    return clone;
                  });

                  setnewProduct((prevState) => ({
                    ...prevState,
                    options: newProductOptions,
                  }));
                  setindexOn(null);
                }
              }}
            >
              <Entypo name="chevron-down" style={styles.chevronDown} />
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.moveUpBtn}
              onPress={() => {
                if (newProductOptions.length > 1 && index !== 0) {
                  setnewProductOptions((prev) => {
                    const clone = structuredClone(prev);
                    const f = clone.splice(index, 1)[0];
                    clone.splice(index - 1, 0, f);
                    return clone;
                  });

                  setnewProduct((prevState) => ({
                    ...prevState,
                    options: newProductOptions,
                  }));
                  setindexOn(null);
                }
              }}
            >
              <Entypo name="chevron-up" style={styles.chevronUp} />
            </TouchableOpacity>
            <TouchableOpacity style={styles.duplicateBtn}>
              <MaterialCommunityIcons
                name="content-copy"
                style={styles.duplicateIcon}
              />
            </TouchableOpacity>
            <TouchableOpacity style={styles.deleteBtn}>
              <MaterialCommunityIcons
                name="delete-outline"
                style={styles.deleteIcon}
              />
            </TouchableOpacity>
          </View>
        </TouchableOpacity>
        {indexOn === index && (
          <View style={styles.innerOptionContainer1}>
            <View style={styles.optionMainInfoRow1}>
              <View style={styles.optionNameInputGroup1}>
                <Text style={styles.optionNameInputLbl1}>Option Name</Text>
                <View style={styles.optionNameInput2}></View>
              </View>
              <View style={styles.optionTypeGroup1}>
                <Text style={styles.optionTypeDropdownLbl1}>Option Type</Text>
                <View style={styles.optionTypeDropdown2}></View>
              </View>
              <View style={styles.selectionLimitInputGroup1}>
                <Text style={styles.selectionLimitInputLbl1}>
                  Selection Limit
                </Text>
                <View style={styles.selectionLimitInput2}></View>
              </View>
            </View>
            <View style={styles.spacer5}></View>
            <View style={styles.optionRequiredRow1}>
              <Text style={styles.isOptionTxt1}>Is Option Required?:</Text>
              <View style={styles.isRequiredBtn1}></View>
            </View>
            <View style={styles.spacer6}></View>
            <OptionSelectionItem
              style={styles.optionSelectionItem1}
            ></OptionSelectionItem>
            <View style={styles.spacer7}></View>
            <View style={styles.addAnotherSelectionBtnRow1}>
              <View style={styles.addAnotherSelectionBtn2}>
                <Text style={styles.addAnotherSelectionBtnLbl1}>
                  Add Another Selection
                </Text>
              </View>
            </View>
          </View>
        )}
      </View>
      {index === newProduct.options.length - 1 && (
        <TouchableOpacity
          onPress={() => {
            setnewProductOptions((prev) => {
              const clone = structuredClone(prev);
              clone.push({
                label: null,
                optionsList: [],
                selectedCaseKey: null,
                selectedCaseValue: null,
                numOfSelectable: null,
                id: Math.random().toString(36).substr(2, 9),
                optionType: null,
              });
              return clone;
            });
            setindexOn(newProductOptions.length);
          }}
          disabled={e.label === null}
          style={styles.createOptionBtn}
        >
          <Text style={styles.createOptionTxt}>Create Option</Text>
        </TouchableOpacity>
      )}
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    borderWidth: 1,
    borderColor: "#000000",
    backgroundColor: "rgba(255,255,255,1)",
    justifyContent: "flex-start",
    marginBottom: 30,
  },
  innerOptionContainer1: {
    width: 808,
    height: 389,
    justifyContent: "flex-start",
    alignItems: "flex-start",
    marginTop: 20,
    marginLeft: 20,
  },
  optionMainInfoRow1: {
    width: 808,
    height: 84,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  optionNameInputGroup1: {
    width: 239,
    height: 84,
    justifyContent: "space-between",
  },
  optionNameInputLbl1: {
    color: "#121212",
    fontSize: 17,
  },
  optionNameInput2: {
    width: 239,
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5,
  },
  optionTypeGroup1: {
    width: 197,
    height: 77,
    justifyContent: "space-between",
  },
  optionTypeDropdownLbl1: {
    color: "#121212",
    fontSize: 17,
  },
  optionTypeDropdown2: {
    width: 195,
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5,
  },
  selectionLimitInputGroup1: {
    width: 195,
    height: 77,
    justifyContent: "space-between",
  },
  selectionLimitInputLbl1: {
    color: "#121212",
    fontSize: 17,
  },
  selectionLimitInput2: {
    width: 195,
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5,
  },
  spacer5: {
    width: 808,
    height: 40,
  },
  optionRequiredRow1: {
    width: 216,
    height: 20,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  isOptionTxt1: {
    fontWeight: "700",
    color: "#121212",
    fontSize: 17,
  },
  isRequiredBtn1: {
    width: 44,
    height: 20,
    backgroundColor: "#E6E6E6",
  },
  spacer6: {
    width: 808,
    height: 53,
  },
  optionSelectionItem1: {
    height: 84,
    width: 808,
  },
  spacer7: {
    width: 808,
    height: 61,
  },
  addAnotherSelectionBtnRow1: {
    height: 47,
    alignSelf: "stretch",
    alignItems: "center",
    justifyContent: "center",
  },
  addAnotherSelectionBtn2: {
    width: 173,
    height: 47,
    backgroundColor: "#1c294e",
    borderRadius: 20,
    alignItems: "center",
    justifyContent: "center",
  },
  addAnotherSelectionBtnLbl1: {
    color: "rgba(255,255,255,1)",
    fontSize: 15,
  },
  createOptionBtn: {
    width: 173,
    height: 47,
    backgroundColor: "#1c294e",
    borderRadius: 20,
    alignItems: "center",
    justifyContent: "center",
  },
  createOptionTxt: {
    fontWeight: "700",
    color: "rgba(255,255,255,1)",
    fontSize: 20,
  },
  closedOptionContainer: {
    backgroundColor: "rgba(255,255,255,1)",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    width: "100%",
    height: 67,
  },
  optionNameLbl: {
    color: "#121212",
    fontSize: 17,
    marginRight: 20,
    marginLeft: 20,
  },
  btnsRow: {
    width: 224,
    height: 35,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginRight: 20,
    marginLeft: 20,
  },
  moveUpBtn: {
    width: 35,
    height: 35,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  chevronUp: {
    color: "rgba(255,255,255,1)",
    fontSize: 30,
  },
  moveDownBtn: {
    width: 35,
    height: 35,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  chevronDown: {
    color: "rgba(255,255,255,1)",
    fontSize: 30,
  },
  duplicateBtn: {
    width: 35,
    height: 35,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  duplicateIcon: {
    color: "rgba(255,255,255,1)",
    fontSize: 26,
  },
  deleteBtn: {
    width: 35,
    height: 35,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  deleteIcon: {
    color: "rgba(255,255,255,1)",
    fontSize: 26,
  },
});

export default OptionsItem;
