import React, { Component } from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import { Feather } from "@expo/vector-icons";

function HoursItem(props) {
  return (
    <View style={[styles.container, props.style]}>
      <View style={styles.checkboxGroup}>
        <View style={styles.checkbox}></View>
      </View>
      <Text style={styles.enteredDateTxt}>{date.toDateString()}</Text>
      <Text style={styles.enteredInTimeTxt}>12:00</Text>
      <Text style={styles.enteredOutTimeTxt1}>18:00</Text>
      <View style={styles.optionsRow}>
        <Feather name="trash" style={styles.trashIcon}></Feather>
        <TouchableOpacity style={styles.markedAsPaidBtn}>
          <Text style={styles.markAsPaidTxt}>Mark as Paid</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    alignItems: "center",
  },
  checkboxGroup: {
    width: 233,
    height: 11,
    justifyContent: "center",
  },
  checkbox: {
    width: 12,
    height: 12,
    backgroundColor: "#E6E6E6",
    marginLeft: 20,
  },
  enteredDateTxt: {
    color: "#121212",
    width: 210,
    height: 17,
  },
  enteredInTimeTxt: {
    color: "#121212",
    width: 210,
    height: 17,
  },
  enteredOutTimeTxt1: {
    color: "#121212",
    width: 170,
    height: 17,
  },
  optionsRow: {
    width: 165,
    height: 41,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  trashIcon: {
    color: "#eb1f1e",
    fontSize: 30,
  },
  markedAsPaidBtn: {
    width: 112,
    height: 41,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  markAsPaidTxt: {
    fontWeight: "700",
    color: "#ffffff",
  },
});

export default HoursItem;
