import {
  Modal,
  ScrollView,
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
} from "react-native";
import React, { useEffect, useState } from "react";
import {
  cartState,
  setCartState,
  storeDetailState,
  setIsSignedInSettingsState,
  customersList,
  setCustomersList,
} from "state/state";
import DeliveryScreen from "components/DeliveryScreen";
import CashScreen from "components/CashScreen";
import ChangeScreen from "components/ChangeScreen";
import { updateTransList } from "state/firebaseFunctions";
import Ionicons from "@expo/vector-icons/Ionicons";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import Feather from "@expo/vector-icons/Feather";
import SaveCustomer from "./SaveCustomer";
import { auth, db } from "state/firebaseConfig";
import Entypo from "@expo/vector-icons/Entypo";
import { useHistory } from "react-router-dom";
import CompletePaymentPhoneOrder from "components/CompletePaymentPhoneOrder";
import SettingsPasswordModal from "components/SettingsPasswordModal";
import CartItemEditable from "components/CartItemEditable";
import useWindowDimensions from "components/useWindowDimensions";

const CartButton = (props) => {
  return (
    <TouchableOpacity
      style={props.style}
      onPress={props.onPress}
      disabled={props.disabled}
    >
      <props.icon />
    </TouchableOpacity>
  );
};

const CartScreen = ({ navigation }) => {
  const [deliveryModal, setDeliveryModal] = useState(false);
  const [cashModal, setCashModal] = useState(false);
  const [changeModal, setChangeModal] = useState(false);
  const [ongoingDelivery, setOngoingDelivery] = useState(null);
  const [name, setName] = useState(null);
  const [phone, setPhone] = useState(null);
  const [address, setAddress] = useState(null);
  const [deliveryChecked, setDeliveryChecked] = useState(false);
  const [changeDue, setChangeDue] = useState(null);
  const cart = cartState.use();
  const storeDetails = storeDetailState.use();
  const [cartSub, setCartSub] = useState(0);
  const [saveCustomerModal, setSaveCustomerModal] = useState(false);
  const [savedCustomerDetails, setsavedCustomerDetails] = useState(null);
  const [ongoingOrderListModal, setongoingOrderListModal] = useState(false);
  const [settingsPasswordModalVis, setsettingsPasswordModalVis] =
    useState(false);
  const history = useHistory();
  const height = useWindowDimensions().height;
  const [updatingOrder, setupdatingOrder] = useState(false);
  const [ongoingListState, setongoingListState] = useState([]);
  const customers = customersList.use();

  useEffect(() => {
    const unsub = db
      .collection("users")
      .doc(auth.currentUser?.uid)
      .collection("pendingOrders")
      .onSnapshot((snapshot) => {
        const list = [];
        snapshot.forEach((doc) => {
          list.push({ ...doc.data(), id: doc.id });
        });
        setongoingListState(list);
      });

    return () => unsub();
  }, [ongoingListState]);

  useEffect(() => {
    // console.log("cart", cart);
    if (cart.length > 0) {
      let newVal = 0;
      for (let i = 0; i < cart.length; i++) {
        try {
          if (cart[i].quantity > 1) {
            newVal += parseFloat(cart[i].price) * cart[i].quantity;
            // console.log("Cart item quantity ", cart[i].quantity);
          } else {
            newVal += parseFloat(cart[i].price);
          }
        } catch (error) {
          console.log(error);
        }
      }
      if (deliveryChecked) {
        setCartSub(newVal + parseFloat(storeDetails.deliveryPrice));
      } else {
        setCartSub(newVal);
      }
    } else {
      setCartSub(0);
    }
  }, [cart]);

  // const AddToList = async (payload) => {
  //   updateTransList(payload);
  // };

  const Print = (method, dontAddToOngoing) => {
    if (savedCustomerDetails) {
      if (savedCustomerDetails.orders?.length > 0) {
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("customers")
          .doc(savedCustomerDetails.id)
          .update({
            orders: [...savedCustomerDetails.orders, { cart }],
          });
        const indexOfCustomer = customers.findIndex(
          (customer) => customer.id === savedCustomerDetails.id
        );
        const newCustomers = structuredClone(customers);
        newCustomers[indexOfCustomer].orders.push({ cart });
        setCustomersList(newCustomers);
      } else {
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("customers")
          .doc(savedCustomerDetails.id)
          .update({
            orders: [{ cart }],
          });
        const indexOfCustomer = customers.findIndex(
          (customer) => customer.id === savedCustomerDetails.id
        );
        const newCustomers = structuredClone(customers);
        newCustomers[indexOfCustomer].orders = [{ cart }];
        setCustomersList(newCustomers);
      }
    }

    const transNum = Math.random().toString(36).substr(2, 9);
    if (method === "deliveryOrder") {
      let total = cartSub;
      const today = new Date();

      const data = [
        "\x1B" + "\x40", // init
        "\x1B" + "\x61" + "\x31", // center align
        storeDetails.name,
        "\x0A",
        storeDetails.address?.label + "\x0A",
        storeDetails.website + "\x0A", // text and line break
        storeDetails.phoneNumber + "\x0A", // text and line break
        today.toLocaleDateString() + " " + today.toLocaleTimeString() + "\x0A",
        "\x0A",
        `Transaction ID ${transNum}` + "\x0A",
        "\x0A",
        `Delivery Order: $${
          storeDetails.deliveryPrice ? storeDetails.deliveryPrice : "0"
        } Fee` + "\x0A",
        "\x0A",
        "\x0A",
        "\x0A",
        "\x1B" + "\x61" + "\x30", // left align
      ];

      cart.map((cartItem) => {
        data.push(`Name: ${cartItem.name}`);
        data.push("\x0A");

        if (cartItem.quantity > 1) {
          total += parseFloat(cartItem.price) * cartItem.quantity;
          data.push(`Quantity: ${cartItem.quantity}`);
          data.push("\x0A");
          data.push(`Price: $${cartItem.price * cartItem.quantity}`);
        } else {
          total += parseFloat(cartItem.price);
          data.push(`Price: $${cartItem.price}`);
        }

        if (cartItem.description) {
          data.push("\x0A");
          data.push(cartItem.description);
        }

        if (cartItem.options) {
          data.push("\x0A");
          cartItem.options.map((option) => {
            data.push(option);
            data.push("\x0A");
          });
        }

        if (cartItem.extraDetails) {
          data.push(cartItem.extraDetails);
          data.push("\x0A");
        }

        data.push("\x0A" + "\x0A");
      });

      total = storeDetails.taxRate
        ? total * (1 + storeDetails.taxRate / 100)
        : total * 1.13;
      total = total.toFixed(2);

      //push ending
      data.push(
        "\x0A",
        "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
        "\x0A" + "\x0A",
        "Customer Name: " + name,
        "\x0A" + "\x0A",
        "Customer Phone #:  " + phone,
        "\x0A" + "\x0A",
        "Customer Address #:  " + address.label,
        "\x0A" + "\x0A",
        `Total Including (${
          storeDetails.taxRate ? storeDetails.taxRate : "13"
        }% Tax): ` +
          total +
          "\x0A" +
          "\x0A",
        "------------------------------------------" + "\x0A",
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x1D" + "\x56" + "\x30"
      );
      const qz = require("qz-tray");
      qz.websocket
        .connect()
        .then(function () {
          const config = qz.configs.create(storeDetails.comSelected);
          return qz.print(config, data);
        })
        .then(qz.websocket.disconnect)
        .catch(function (err) {
          console.error(err);
        });

      // AddToList({
      //   id: Math.random().toString(36).substr(2, 9) + "-l",
      //   date: today,
      //   transNum: transNum,
      //   total: total,
      //   method: "deliveryOrder",
      //   cart: cart,
      //   // completed: false,
      //   customer: {
      //     name: name,
      //     phone: phone,
      //     address: address ? address : null,
      //   },
      // });
      if (!dontAddToOngoing) {
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("pendingOrders")
          .add({
            id: Math.random().toString(36).substr(2, 9) + "-l",
            date: today,
            transNum: transNum,
            total: total,
            method: "deliveryOrder",
            cart: cart,
            customer: {
              name: name,
              phone: phone,
              address: address ? address : null,
            },
          })
          .then((docRef) => {
            setongoingListState([
              ...ongoingListState,
              {
                id: docRef.id,
                date: today,
                transNum: transNum,
                total: total,
                method: "deliveryOrder",
                cart: cart,
                customer: {
                  name: name,
                  phone: phone,
                  address: address ? address : null,
                },
              },
            ]);
          });
      }

      setCartState([]);
      setDeliveryModal(false);
    } else if (method === "pickupOrder") {
      let total = 0;
      const today = new Date();

      const data = [
        "\x1B" + "\x40", // init
        "\x1B" + "\x61" + "\x31", // center align
        storeDetails.name,
        "\x0A",
        storeDetails.address?.label + "\x0A",
        storeDetails.website + "\x0A", // text and line break
        storeDetails.phoneNumber + "\x0A", // text and line break
        today.toLocaleDateString() + " " + today.toLocaleTimeString() + "\x0A",
        "\x0A",
        `Transaction ID ${transNum}` + "\x0A",
        "\x0A",
        "Pickup Order" + "\x0A",
        "\x0A",
        "\x0A",
        "\x0A",
        "\x1B" + "\x61" + "\x30", // left align
      ];

      cart.map((cartItem) => {
        data.push(`Name: ${cartItem.name}`);
        data.push("\x0A");

        if (cartItem.quantity > 1) {
          total += parseFloat(cartItem.price) * cartItem.quantity;
          data.push(`Quantity: ${cartItem.quantity}`);
          data.push("\x0A");
          data.push(`Price: $${cartItem.price * cartItem.quantity}`);
        } else {
          total += parseFloat(cartItem.price);
          data.push(`Price: $${cartItem.price}`);
        }

        if (cartItem.description) {
          data.push("\x0A");
          data.push(cartItem.description);
        }

        if (cartItem.options) {
          data.push("\x0A");
          cartItem.options.map((option) => {
            data.push(option);
            data.push("\x0A");
          });
        }

        if (cartItem.extraDetails) {
          data.push(cartItem.extraDetails);
          data.push("\x0A");
        }

        data.push("\x0A" + "\x0A");
      });

      total = total * 1.13;
      total = total.toFixed(2);

      //push ending
      data.push(
        "\x0A",
        "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
        "\x0A" + "\x0A",
        "Customer Name: " + name,
        "\x0A" + "\x0A",
        "Customer Phone #:  " + phone,
        "\x0A" + "\x0A",
        `Total Including (${
          storeDetails.taxRate ? storeDetails.taxRate : "13"
        }% Tax): ` +
          total +
          "\x0A" +
          "\x0A",
        "------------------------------------------" + "\x0A",
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x0A", // line break
        "\x1D" + "\x56" + "\x30"
      );
      // fetch("http://localhost:8080/print", {
      //   method: "POST",
      //   headers: {
      //     "Content-Type": "application/json",
      //   },
      //   body: JSON.stringify({
      //     printData: data,
      //     comSelected: storeDetails.comSelected,
      //   }),
      // })
      //   .then((response) => response.json())
      //   .then((respData) => {
      //     console.log(respData);
      //   })
      //   .catch((e) => alert("Error with printer"));
      const qz = require("qz-tray");
      qz.websocket
        .connect()
        .then(function () {
          const config = qz.configs.create(storeDetails.comSelected);
          return qz.print(config, data);
        })
        .then(qz.websocket.disconnect)
        .catch(function (err) {
          console.error(err);
        });

      // AddToList({
      //   id: Math.random().toString(36).substr(2, 9) + "-l",
      //   date: today,
      //   transNum: transNum,
      //   total: total,
      //   method: "pickupOrder",
      //   cart: cart,
      //   customer: {
      //     name: name,
      //     phone: phone,
      //     address: address ? address : null,
      //   },
      // });
      if (!dontAddToOngoing) {
        const ongoingList = JSON.parse(localStorage.getItem("ongoingList"));
        if (ongoingList) {
          ongoingList.push({
            id: Math.random().toString(36).substr(2, 9) + "-l",
            date: today,
            transNum: transNum,
            total: total,
            method: "pickupOrder",
            cart: cart,
            customer: {
              name: name,
              phone: phone,
              address: address ? address : null,
            },
          });
          localStorage.setItem("ongoingList", JSON.stringify(ongoingList));
          setongoingListState(ongoingList);
        } else {
          localStorage.setItem(
            "ongoingList",
            JSON.stringify([
              {
                id: Math.random().toString(36).substr(2, 9) + "-l",
                date: today,
                transNum: transNum,
                total: total,
                method: "pickupOrder",
                cart: cart,
                customer: {
                  name: name,
                  phone: phone,
                  address: address ? address : null,
                },
              },
            ])
          );
          // setongoingListState([
          //   {
          //     id: Math.random().toString(36).substr(2, 9) + "-l",
          //     date: today,
          //     transNum: transNum,
          //     total: total,
          //     method: "pickupOrder",
          //     cart: cart,
          //     customer: {
          //       name: name,
          //       phone: phone,
          //       address: address ? address : null,
          //     },
          //   },
          // ]);
        }
      }

      setCartState([]);
      setDeliveryModal(false);
      setName(null);
      setPhone(null);
      setAddress(null);
      setDeliveryChecked(false);
    } else {
      let total = 0;
      const today = new Date();

      const data = [
        "\x1B" + "\x40", // init
        "\x1B" + "\x61" + "\x31", // center align
        storeDetails.name,
        "\x0A",
        storeDetails.address?.label + "\x0A",
        storeDetails.website + "\x0A", // text and line break
        storeDetails.phoneNumber + "\x0A", // text and line break
        today.toLocaleDateString() + " " + today.toLocaleTimeString() + "\x0A",
        "\x0A",
        `Transaction ID ${transNum}` + "\x0A",
        "\x0A",
        "\x0A",
        "\x0A",
        "\x1B" + "\x61" + "\x30", // left align
      ];

      cart.map((cartItem) => {
        data.push(`Name: ${cartItem.name}`);
        data.push("\x0A");

        if (cartItem.quantity > 1) {
          total += parseFloat(cartItem.price) * cartItem.quantity;
          data.push(`Quantity: ${cartItem.quantity}`);
          data.push("\x0A");
          data.push(`Price: $${cartItem.price * cartItem.quantity}`);
        } else {
          total += parseFloat(cartItem.price);
          data.push(`Price: $${cartItem.price}`);
        }

        if (cartItem.description) {
          data.push("\x0A");
          data.push(cartItem.description);
        }

        if (cartItem.options) {
          data.push("\x0A");
          cartItem.options.map((option) => {
            data.push(option);
            data.push("\x0A");
          });
        }

        if (cartItem.extraDetails) {
          data.push(cartItem.extraDetails);
          data.push("\x0A");
        }

        data.push("\x0A" + "\x0A");
      });

      total = total * 1.13;
      total = total.toFixed(2);

      if (method === "Cash") {
        //push ending
        data.push(
          "\x0A",
          "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
          "\x0A" + "\x0A",
          "Payment Method: " + method + "\x0A" + "\x0A",
          `Total Including (${
            storeDetails.taxRate ? storeDetails.taxRate : "13"
          }% Tax): ` +
            "$" +
            total +
            "\x0A" +
            "\x0A",
          "Change Due: " + "$" + changeDue + "\x0A" + "\x0A",
          "------------------------------------------" + "\x0A",
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          //"\x1D" + "\x56" + "\x00",
          "\x1D" + "\x56" + "\x30" + "\x10" + "\x14" + "\x01" + "\x00" + "\x05"
        );
      } else {
        data.push(
          "\x0A",
          "XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX" + "\x0A",
          "\x0A" + "\x0A",
          "Payment Method: " + method + "\x0A" + "\x0A",
          `Total Including (${
            storeDetails.taxRate ? storeDetails.taxRate : "13"
          }% Tax): ` +
            "$" +
            total +
            "\x0A" +
            "\x0A",
          "------------------------------------------" + "\x0A",
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          "\x0A", // line break
          //"\x1D" + "\x56" + "\x00",
          "\x1D" + "\x56" + "\x30"
        );
      }

      const qz = require("qz-tray");
      qz.websocket
        .connect()
        .then(function () {
          const config = qz.configs.create(storeDetails.comSelected);
          return qz.print(config, data);
        })
        .then(qz.websocket.disconnect)
        .catch(function (err) {
          console.error(err);
        });

      // AddToList({
      //   id: Math.random().toString(36).substr(2, 9) + "-l",
      //   date: today,
      //   transNum: transNum,
      //   total: total,
      //   method: method,
      //   cart: cart,
      // });
    }

    setCartState([]);
    setName(null);
    setPhone(null);
    setAddress(null);
    setDeliveryChecked(false);
    setChangeDue(null);
  };

  const DeliveryBtn = () => {
    if (updatingOrder) {
      return (
        <TouchableOpacity
          style={[styles.bigButton, cartSub === 0 && { opacity: 0.5 }]}
          disabled={cart.length < 1}
          onPress={() => {
            // const oldList = structuredClone(
            //   JSON.parse(localStorage.getItem("ongoingList"))
            // );
            // oldList[updatingOrder.index].cart = cart;
            // setongoingListState(oldList);


            Print(deliveryChecked ? "deliveryOrder" : "pickupOrder", true);
            setOngoingDelivery(null);
            setName(null);
            setPhone(null);
            setAddress(null);
            setDeliveryChecked(false);
            setupdatingOrder(false);
          }}
        >
          <Text style={styles.btnTxt}>Update Order</Text>
        </TouchableOpacity>
      );
    }

    if (ongoingDelivery === null) {
      return (
        <View
          style={{
            flexDirection: "row",
            width: "100%",
            alignSelf: "center",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 10,
          }}
        >
          <TouchableOpacity
            style={[styles.cashButton, cartSub === 0 && { opacity: 0.5 }]}
            onPress={() => setCashModal(true)}
            disabled={cart.length < 1 || ongoingDelivery}
          >
            <Text style={styles.btnTxt}>Cash</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={[styles.cardButton, cartSub === 0 && { opacity: 0.5 }]}
            onPress={() => Print("Card")}
            disabled={cart.length < 1 || ongoingDelivery}
          >
            <Text style={styles.btnTxt}>Card</Text>
          </TouchableOpacity>
        </View>
      );
    }
    if (ongoingDelivery && cart.length > 0) {
      return (
        <TouchableOpacity
          style={styles.bigButton}
          onPress={() => {
            Print(deliveryChecked ? "deliveryOrder" : "pickupOrder");
            setOngoingDelivery(null);
            setName(null);
            setPhone(null);
            setAddress(null);
          }}
        >
          <Text style={styles.btnTxt}>Complete</Text>
        </TouchableOpacity>
      );
    } else {
      return (
        <TouchableOpacity
          style={styles.bigButton}
          onPress={() => {
            setOngoingDelivery(null);
            setDeliveryChecked(false);
            setName(null);
            setPhone(null);
            setAddress(null);
            setChangeDue(null);
          }}
        >
          <Text style={styles.btnTxt}>Cancel</Text>
        </TouchableOpacity>
      );
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.cartHeader}>
        <Text
          style={{
            fontFamily: "archivo-600",
            color: "rgba(255,255,255,1)",
            fontSize: 25,
          }}
        >
          Order
        </Text>
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "space-between",
          }}
        >
          <CartButton
            style={[
              styles.iconContainer,
              (cart.length > 0 || updatingOrder) && { opacity: 0.5 },
            ]}
            onPress={() => setSaveCustomerModal(true)}
            disabled={cart.length > 0 || updatingOrder}
            icon={() => (
              <MaterialCommunityIcons name="history" size={26} color="white" />
            )}
          />
          <CartButton
            style={[
              styles.iconContainer,
              (cart.length > 0 || updatingOrder) && { opacity: 0.5 },
            ]}
            disabled={cart.length > 0 || updatingOrder}
            onPress={() => setongoingOrderListModal(true)}
            icon={() => (
              <Ionicons name="chevron-down" size={28} color="white" />
            )}
          />
          <CartButton
            style={[
              styles.iconContainer,
              cart.length > 0 && !ongoingDelivery && { opacity: 0.5 },
            ]}
            onPress={() => setDeliveryModal(true)}
            disabled={cart.length > 0 && !ongoingDelivery}
            icon={() => <Feather name="phone-call" size={28} color="white" />}
          />
          {updatingOrder ? (
            <CartButton
              style={[styles.iconContainer]}
              onPress={() => {
                setCartState([]);
                setName(null);
                setPhone(null);
                setAddress(null);
                setDeliveryChecked(false);
                setOngoingDelivery(null);
                setongoingOrderListModal(false);
                setupdatingOrder(false);
              }}
              icon={() => <Entypo name="cross" size={28} color="white" />}
            />
          ) : (
            <CartButton
              disabled={ongoingDelivery}
              style={[
                styles.iconContainer,
                cart.length > 0 && { opacity: 0.5 },
              ]}
              onPress={() => {
                if (storeDetails.settingsPassword) {
                  setsettingsPasswordModalVis(true);
                } else {
                  setIsSignedInSettingsState(true);
                  history.push("/authed/dashboard");
                  localStorage.setItem("isAuthedBackend", true);
                }
              }}
              icon={() => <Entypo name="cog" size={28} color="white" />}
            />
          )}
        </View>
      </View>
      <ScrollView
        contentContainerStyle={styles.contentContainer}
        style={{ marginBottom: 10 }}
      >
        <View>
          {cart.length > 0 ? (
            cart.map((cartItem, index) => (
              <CartItemEditable
                key={index}
                cartItem={cartItem}
                index={index}
                removeAction={() => {
                  console.log("Removing");
                  const local = structuredClone(cart);
                  local.splice(index, 1);
                  setCartState(local);
                }}
                decreaseAction={() => {
                  const local = structuredClone(cart);
                  local[index].quantity--;
                  setCartState(local);
                }}
                increaseAction={() => {
                  const local = structuredClone(cart);
                  if (local[index].quantity) {
                    local[index].quantity++;
                  } else {
                    local[index].quantity = 2;
                  }
                  setCartState(local);
                }}
              />
            ))
          ) : (
            <View
              style={{
                height: height * 0.7,
                width: "100%",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <Text style={styles.empty}>Empty...</Text>
              <Text style={styles.fillTheCart}>Fill the Cart!</Text>
            </View>
          )}
        </View>
      </ScrollView>
      <View
        style={{
          justifyContent: "space-around",
          alignItems: "center",
          width: "90%",
          alignSelf: "center",
        }}
      >
        {deliveryChecked && parseFloat(storeDetails.deliveryPrice) && (
          <View
            style={{
              alignSelf: "stretch",
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: 2,
              paddingTop: 5,
            }}
          >
            <Text
              style={{
                fontFamily: "archivo-600",
                color: "rgba(74,74,74,1)",
                fontSize: 16,
                alignSelf: "flex-start",
              }}
            >
              Delivery
            </Text>
            <Text
              style={[
                {
                  fontFamily: "archivo-600",
                  color: "rgba(255,255,255,1)",
                  fontSize: 20,
                  alignSelf: "flex-start",
                },
                cartSub === 0 && { opacity: 0.5 },
              ]}
            >
              ${parseFloat(storeDetails.deliveryPrice).toFixed(2)}
            </Text>
          </View>
        )}
        <View
          style={{
            alignSelf: "stretch",
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 2,
          }}
        >
          <Text
            style={{
              fontFamily: "archivo-600",
              color: "rgba(74,74,74,1)",
              fontSize: 16,
              alignSelf: "flex-start",
            }}
          >
            Sub Total
          </Text>
          <Text
            style={[
              {
                fontFamily: "archivo-600",
                color: "rgba(255,255,255,1)",
                fontSize: 20,
                alignSelf: "flex-start",
              },
              cartSub === 0 && { opacity: 0.5 },
            ]}
          >
            ${cartSub.toFixed(2)}
          </Text>
        </View>
        <View
          style={{
            alignSelf: "stretch",
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 2,
          }}
        >
          <Text
            style={{
              fontFamily: "archivo-600",
              color: "rgba(74,74,74,1)",
              fontSize: 16,
              alignSelf: "flex-start",
            }}
          >
            Tax
          </Text>
          <Text
            style={[
              {
                fontFamily: "archivo-600",
                color: "rgba(255,255,255,1)",
                fontSize: 20,
                alignSelf: "flex-start",
              },
              cartSub === 0 && { opacity: 0.5 },
            ]}
          >
            $
            {(
              cartSub *
              (storeDetails.taxRate ? storeDetails.taxRate / 100 : 0.13)
            ).toFixed(2)}
          </Text>
        </View>
        <View
          style={{
            alignSelf: "stretch",
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 10,
          }}
        >
          <Text
            style={{
              fontFamily: "archivo-600",
              color: "rgba(74,74,74,1)",
              fontSize: 16,
              alignSelf: "flex-start",
            }}
          >
            Total
          </Text>
          <Text
            style={[
              {
                fontFamily: "archivo-600",
                color: "rgba(255,255,255,1)",
                fontSize: 20,
                alignSelf: "flex-start",
              },
              cartSub === 0 && { opacity: 0.5 },
            ]}
          >
            ${(cartSub * 1.13).toFixed(2)}
          </Text>
        </View>
        <DeliveryBtn />
      </View>
      <Modal visible={saveCustomerModal} transparent={true}>
        <SaveCustomer
          setSaveCustomerModal={setSaveCustomerModal}
          setOngoingDelivery={setOngoingDelivery}
          setNameForDelivery={setName}
          setPhoneForDelivery={setPhone}
          setAddressForDelivery={setAddress}
          setDeliveryChecked={setDeliveryChecked}
          setsavedCustomerDetails={setsavedCustomerDetails}
        />
      </Modal>
      <Modal visible={deliveryModal} transparent={true}>
        <DeliveryScreen
          setsavedCustomerDetails={setsavedCustomerDetails}
          setDeliveryModal={setDeliveryModal}
          setOngoingDelivery={setOngoingDelivery}
          setName={setName}
          setPhone={setPhone}
          setAddress={setAddress}
          name={name}
          phone={phone}
          address={address}
          deliveryChecked={deliveryChecked}
          setDeliveryChecked={setDeliveryChecked}
          ongoingDelivery={ongoingDelivery}
        />
      </Modal>
      <Modal visible={cashModal} transparent>
        <CashScreen
          setCashModal={setCashModal}
          GetTrans={() => Print("Cash")}
          total={(cartSub * 1.13).toFixed(2)}
          setChangeDue={setChangeDue}
        />
      </Modal>
      <Modal visible={changeModal}>
        <ChangeScreen setChangeModal={setChangeModal} />
      </Modal>
      <Modal visible={ongoingOrderListModal} transparent={true}>
        <CompletePaymentPhoneOrder
          setongoingOrderListModal={setongoingOrderListModal}
          updateOrderHandler={(order) => {
            setCartState(order.cart);
            setName(order.customer.name);
            setPhone(order.customer.phone);
            setAddress(order.customer.address);
            setDeliveryChecked(order.method === "deliveryOrder");
            setOngoingDelivery(true);
            setongoingOrderListModal(false);
            setupdatingOrder(order);
          }}
          ongoingListState={ongoingListState}
          setongoingListState={setongoingListState}
        />
      </Modal>
      <Modal visible={settingsPasswordModalVis} transparent={true}>
        <SettingsPasswordModal
          setsettingsPasswordModalVis={setsettingsPasswordModalVis}
          navigation={navigation}
        />
      </Modal>
    </View>
  );
};

