import {
  Modal,
  ScrollView,
  StyleSheet,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useEffect, useLayoutEffect, useState } from "react";
import { Button, Text } from "@react-native-material/core";
import { storeDetailState, transListState } from "state/state";
import { updateTransList } from "state/firebaseFunctions";
import ChangeScreen from "./ChangeScreen";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import useWindowDimensions from "./useWindowDimensions";

const SettingsPasswordModal = ({ settingsPasswordModalVis }) => {
  const { height, width } = useWindowDimensions();

  return (
    <View
      style={{
        backgroundColor: "rgba(0, 0, 0, 0.3)",
        justifyContent: "center",
        alignItems: "center",
        height: height,
        width: width,
      }}
    >
      <View
        style={{
          backgroundColor: "white",
          height: height * 0.7,
          width: height * 0.7,
          borderRadius: 30,
          shadowColor: "rgba(0,0,0,1)",
          shadowOffset: {
            width: 3,
            height: 3,
          },
          elevation: 30,
          shadowOpacity: 0.57,
          shadowRadius: 10,
        }}
      >
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
          }}
        >
          <MaterialCommunityIcons
            onPress={() => settingsPasswordModalVis(false)}
            name="close"
            size={26}
            color="rgba(74,74,74,1)"
            style={{ width: "25%", marginLeft: 20 }}
          />
          <Text
            style={{
              margin: 25,
              fontSize: 20,
              fontWeight: "600",
              width: "50%",
              textAlign: "center",
            }}
          >
            Pending Orders
          </Text>
          <View style={{ width: "25%" }} />
        </View>

        <Modal visible={changeModal} transparent={true}>
          <ChangeScreen
            setChangeModal={setChangeModal}
            setcurrentOrder={setcurrentOrder}
            order={currentOrder.element}
            completeOrder={() => {
              const localChange = structuredClone(transList);
              localChange[currentOrder.index].completed = true;
              updateTransList(localChange);
              setChangeModal(false);
            }}
            goBack={() => {
              setcurrentOrder({
                element: null,
                index: null,
              });
              setChangeModal(false);
            }}
          />
        </Modal>
      </View>
    </View>
  );
};

export default SettingsPasswordModal;

const styles = StyleSheet.create({
  contentContainer: {
    justifyContent: "space-between",
    paddingLeft: 50,
    paddingRight: 50,
    paddingBottom: 50,
    height: "95%",
    width: "100%",
    alignItems: "center",
  },
});
