import { View, Text, Button, Platform } from "react-native";
import React, { useState } from "react";
import * as Linking from "expo-linking";

const RouteSliderScreen = ({ finalList }) => {
  const [index, setindex] = useState(1);

  const OpenMap = () => {
    if (Platform.OS === "web") {
      window.open(finalList[index].url, "_blank");
    } else {
      Linking.openURL(finalList[index].url);
    }
  };

  return (
    <View>
      <Text>Route {index}</Text>
      <Text>{finalList[index].formatted_address}</Text>
      <Button title="Go To Google Maps" onPress={OpenMap} />
      <Button
        title="Back"
        onPress={() => setindex(index - 1)}
        disabled={index === 1}
      />
      <Button
        title="Next"
        onPress={() => setindex(index + 1)}
        disabled={index === finalList.length - 1}
      />
    </View>
  );
};

export default RouteSliderScreen;
