import React, { Component } from "react";
import {
  StyleSheet,
  View,
  Text,
  ScrollView,
  TouchableOpacity,
  TouchableWithoutFeedback,
  useWindowDimensions,
} from "react-native";
import {
  Entypo as EntypoIcon,
  Ionicons,
  FontAwesome as FontAwesomeIcon,
  MaterialCommunityIcons as MaterialCommunityIconsIcon,
  MaterialIcons as MaterialIconsIcon,
} from "@expo/vector-icons";
import OrderItem from "./components/OrderItem";
import { db } from "state/firebaseConfig";
import { customersList, setCartState, setCustomersList, storeDetailState } from "state/state";

function CustomerDetailsModal({ setcustomerSelected, customerSelected }) {
  const { height, width } = useWindowDimensions();
  const customers = customersList.use();
  const storeDetails = storeDetailState.use();

  return (
    <TouchableOpacity
      onPress={() => {
        setcustomerSelected(null);
      }}
      style={{
        justifyContent: "center",
        alignItems: "center",
        height: height,
        width: width,
      }}
      activeOpacity={1}
    >
      <TouchableWithoutFeedback>
        <div style={{ cursor: "default" }}>
          <View style={styles.container}>
            <View style={styles.topGroup}>
              <View style={styles.topRow}>
                <EntypoIcon
                  name="chevron-left"
                  style={styles.goBackIcon}
                ></EntypoIcon>
                <Ionicons name="md-close" style={styles.closeIcon} />
              </View>
              <Text style={styles.georgesOrders}>
                customerSelected.name's Orders
              </Text>
            </View>
            <View style={styles.bottomContainer}>
              <View style={styles.customerDetailsContainer}>
                <View style={styles.customerPhoneNumberRow}>
                  <FontAwesomeIcon name="phone" style={styles.phoneIcon} />
                  <Text style={styles.phoneNumber}>2266005925</Text>
                </View>
                <View style={styles.addressRow}>
                  <EntypoIcon name="home" style={styles.addressIcon} />
                  <View style={styles.addressGroup}>
                    <Text style={styles.address}>
                      32 Sunrise Drive, Kitchener, ON
                    </Text>
                    <View style={styles.addressExtraDetailsRow}>
                      <Text style={styles.unitNumber}>Unit #: 32</Text>
                      <Text style={styles.buzzCode}>Buzz Code: 123</Text>
                    </View>
                  </View>
                </View>
                <View style={styles.customerOptionsBtnRow}>
                  <MaterialCommunityIconsIcon
                    name="pencil"
                    style={styles.editCustomerIcon}
                  />
                  <TouchableOpacity
                    onPress={() => {
                      db.collection("users")
                        .doc(auth.currentUser?.uid)
                        .collection("customers")
                        .doc(customerSelected.id)
                        .delete();
                      setCustomersList(() =>
                        customers.filter((e) => e.id !== customerSelected.id)
                      );
                      setcustomerSelected(null);
                    }}
                  >
                    <FontAwesomeIcon name="trash" style={styles.deleteIcon} />
                  </TouchableOpacity>
                </View>
              </View>
              <View style={styles.orderScrollView}>
                <ScrollView
                  horizontal={false}
                  contentContainerStyle={
                    styles.orderScrollView_contentContainerStyle
                  }
                >
                  {customerSelected.orders?.map((prevOrder, prevOrderIndex) => (
                    <OrderItem
                      key={prevOrderIndex}
                      prevOrder={prevOrder}
                      prevOrderIndex={prevOrderIndex}
                      setOrderPickUp={() => {
                        setCartState(prevOrder.cart);
                        setOngoingDelivery(true);
                        setNameForDelivery(customerSelected.name);
                        setPhoneForDelivery(customerSelected.phone);
                        setAddressForDelivery(customerSelected.address);
                        setBuzzCodeForDelivery(customerSelected.buzzCode);
                        setUnitNumberForDelivery(customerSelected.unitNumber);
                        setDeliveryChecked(false);
                        setSaveCustomerModal(false);
                      }}
                      setOrderDelivery={() => {
                        setCartState(prevOrder.cart);
                        setOngoingDelivery(true);
                        setNameForDelivery(customerSelected.name);
                        setPhoneForDelivery(customerSelected.phone);
                        setAddressForDelivery(customerSelected.address);
                        setBuzzCodeForDelivery(customerSelected.buzzCode);
                        setUnitNumberForDelivery(customerSelected.unitNumber);
                        setDeliveryChecked(true);
                        setSaveCustomerModal(false);
                      }}
                      isDeliverable={
                        storeDetails.acceptDelivery && customerSelected.address
                      }
                    />
                  ))}
                </ScrollView>
              </View>
              <View style={styles.addNewOrderRow}>
                <Text style={styles.addNewOrder}>+ Add New Order</Text>
              </View>
            </View>
          </View>
        </div>
      </TouchableWithoutFeedback>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "space-around",
    backgroundColor: "rgba(255,255,255,1)",
    width: 540,
    height: 608,
  },
  topGroup: {
    width: 493,
    height: 59,
    alignItems: "center",
    justifyContent: "space-between",
    marginTop: 13,
  },
  topRow: {
    width: 493,
    height: 28,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  goBackIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 40,
  },
  closeIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 40,
  },
  georgesOrders: {
    fontWeight: "700",
    color: "#121212",
    fontSize: 20,
  },
  bottomContainer: {
    width: 454,
    height: 454,
    justifyContent: "space-between",
    alignItems: "flex-start",
  },
  customerDetailsContainer: {
    width: 439,
    height: 157,
    alignItems: "center",
    justifyContent: "space-around",
    borderRadius: 10,
    backgroundColor: "#edf1fe",
  },
  customerPhoneNumberRow: {
    width: 395,
    height: 35,
    flexDirection: "row",
    justifyContent: "flex-start",
    alignItems: "center",
    paddingTop: 0,
    marginTop: 20,
  },
  phoneIcon: {
    color: "#1c294e",
    fontSize: 35,
    paddingRight: 15,
  },
  phoneNumber: {
    color: "#121212",
    fontSize: 15,
  },
  addressRow: {
    width: 395,
    height: 42,
    flexDirection: "row",
    justifyContent: "flex-start",
    alignItems: "center",
    marginTop: 15,
  },
  addressIcon: {
    color: "#1c294e",
    fontSize: 35,
    paddingRight: 10,
  },
  addressGroup: {
    height: 36,
    justifyContent: "space-between",
    alignItems: "center",
  },
  address: {
    color: "#121212",
    fontSize: 15,
  },
  addressExtraDetailsRow: {
    width: 207,
    height: 18,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  unitNumber: {
    color: "#121212",
    fontSize: 15,
  },
  buzzCode: {
    color: "#121212",
    fontSize: 15,
  },
  customerOptionsBtnRow: {
    width: 395,
    height: 35,
    flexDirection: "row",
    justifyContent: "flex-end",
    alignItems: "center",
    marginBottom: 10,
  },
  editCustomerIcon: {
    color: "#1c294e",
    fontSize: 32,
  },
  deleteIcon: {
    color: "#1c294e",
    fontSize: 32,
    paddingLeft: 10,
  },
  orderScrollView: {
    width: 439,
    height: 239,
  },
  orderScrollView_contentContainerStyle: {
    paddingTop: 20,
  },
  addNewOrderRow: {
    width: 439,
    height: 18,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "flex-end",
  },
  addNewOrder: {
    color: "#121212",
    fontSize: 15,
    textDecorationLine: "underline",
  },
});

export default CustomerDetailsModal;
