import React, { Component, useEffect, useRef, useState } from "react";
import { StyleSheet, View, Text, TouchableOpacity } from "react-native";
import OptionSelectionItem from "./OptionSelectionItem";
import { Entypo, MaterialCommunityIcons } from "@expo/vector-icons";
import OptionsItemOpenInner from "./OptionsItemOpenInner";

function OptionsItem({
  item,
  index,
  setnewProduct,
  newProduct,
  newProductOptions,
  setnewProductOptions,
  indexOn,
  setindexOn,
  style,
  scrollY,
  scrollViewRef,
}) {
  const [e, sete] = useState(structuredClone(item));

  const [optionHeights, setOptionHeights] = useState([]);
  const [ifStatementHeights, setIfStatementHeights] = useState([]);
  const [layoutHeight, setLayoutHeight] = useState(0);

  const updateOptionHeight = (height, idx) => {
    setOptionHeights((prevHeights) => {
      const newHeights = [...prevHeights];
      newHeights[idx] = height;
      return newHeights;
    });
  };

  const updateIfStatementHeight = (height, idx) => {
    setIfStatementHeights((prevHeights) => {
      const newHeights = [...prevHeights];
      newHeights[idx] = height;
      return newHeights;
    });
  };

  useEffect(() => {
    if (indexOn === index) {
      const totalOptionHeight = optionHeights.reduce(
        (acc, height) => acc + (height || 0),
        0
      );
      const totalIfStatementHeight = ifStatementHeights.reduce(
        (acc, height) => acc + (height || 0),
        0
      );
      const dynamicContentAdjustment =
        totalOptionHeight + totalIfStatementHeight;

      scrollViewRef.current.measure(
        (fx, fy, width, scrollViewHeight, px, py) => {
          // Calculate the bottom position of the element, including dynamic content
          const elementBottomPosition =
            py + layoutHeight + dynamicContentAdjustment;
          // Calculate the visible bottom position of the ScrollView
          let visibleBottomPosition = scrollY + scrollViewHeight;

          // Adjust visible position by the height of additional dynamic content, if any
          if (index === newProductOptions.length - 1) {
            visibleBottomPosition -= dynamicContentAdjustment;
          }

          // Check if the element is fully visible. If not, scroll to show the element.
          if (elementBottomPosition > visibleBottomPosition || py < scrollY) {
            const positionToScroll = Math.max(
              0,
              elementBottomPosition - scrollViewHeight
            );
            scrollViewRef.current.scrollTo({
              y: positionToScroll,
              animated: true,
            });
          }
        }
      );
    }
  }, [indexOn, optionHeights, ifStatementHeights, scrollY, layoutHeight]); // Only re-run if these values change

  return (
    <>
      <View
        style={[
          styles.container,
          style,
          indexOn === index && { paddingBottom: 20 },
        ]}
        onLayout={(event) => {
          const { y, height } = event.nativeEvent.layout;
          setLayoutHeight(height); // Save the height in the state
          if (indexOn === index) {
            // Get the ScrollView's height and the current scroll position
            scrollViewRef.current.measure(
              (fx, fy, width, scrollViewHeight, px, py) => {
                // When determining where to scroll
                const totalOptionHeight = optionHeights.reduce(
                  (acc, height) => acc + (height || 0),
                  0
                );
                const totalIfStatementHeight = ifStatementHeights.reduce(
                  (acc, height) => acc + (height || 0),
                  0
                );

                const dynamicContentAdjustment =
                  totalOptionHeight + totalIfStatementHeight;

                // Calculate the bottom position of the element, including dynamic content
                const elementBottomPosition =
                  y + height + dynamicContentAdjustment;

                // Calculate the visible bottom position of the ScrollView
                let visibleBottomPosition = scrollY + scrollViewHeight;

                // Adjust visible position by the height of additional dynamic content, if any
                if (index === newProductOptions.length - 1) {
                  visibleBottomPosition -= dynamicContentAdjustment;
                }

                // Check if the element is fully visible. If not, scroll to show the element.
                if (
                  elementBottomPosition > visibleBottomPosition ||
                  y < scrollY
                ) {
                  const positionToScroll = Math.max(
                    0,
                    elementBottomPosition - scrollViewHeight
                  );
                  scrollViewRef.current.scrollTo({
                    y: positionToScroll,
                    animated: true,
                  });
                }
              }
            );
          }
        }}
      >
        <TouchableOpacity
          style={[
            styles.closedOptionContainer,
            index === indexOn && { borderBottomWidth: 1 },
          ]}
          onPress={() =>
            indexOn !== index ? setindexOn(index) : setindexOn(null)
          }
          activeOpacity={0.8}
        >
          <Text style={styles.optionNameLbl}>
            {e.label ? e.label : "New Option"}
          </Text>
          <View style={styles.btnsRow}>
            <TouchableOpacity
              style={styles.moveDownBtn}
              onPress={() => {
                if (
                  newProductOptions.length > 1 &&
                  index !== newProductOptions.length - 1
                ) {
                  setnewProductOptions((prev) => {
                    const clone = structuredClone(prev);
                    const f = clone.splice(index, 1)[0];
                    clone.splice(index + 1, 0, f);
                    return clone;
                  });

                  setnewProduct((prevState) => ({
                    ...prevState,
                    options: newProductOptions,
                  }));
                  setindexOn(null);
                }
              }}
            >
              <Entypo name="chevron-down" style={styles.chevronDown} />
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.moveUpBtn}
              onPress={() => {
                if (newProductOptions.length > 1 && index !== 0) {
                  setnewProductOptions((prev) => {
                    const clone = structuredClone(prev);
                    const f = clone.splice(index, 1)[0];
                    clone.splice(index - 1, 0, f);
                    return clone;
                  });

                  setnewProduct((prevState) => ({
                    ...prevState,
                    options: newProductOptions,
                  }));
                  setindexOn(null);
                }
              }}
            >
              <Entypo name="chevron-up" style={styles.chevronUp} />
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.duplicateBtn}
              onPress={() => {
                setnewProductOptions((prev) => {
                  const clone = structuredClone(prev);
                  clone.push({
                    ...item,
                    label: item.label + " Copy",
                    id: Math.random().toString(36).substr(2, 9),
                  });
                  return clone;
                });

                setnewProduct((prevState) => ({
                  ...prevState,
                  options: newProductOptions,
                }));
                setindexOn(newProductOptions.length);
              }}
            >
              <MaterialCommunityIcons
                name="content-copy"
                style={styles.duplicateIcon}
              />
            </TouchableOpacity>
            <TouchableOpacity
              style={styles.deleteBtn}
              onPress={() => {
                const newProductOptionsUpdated = newProduct.options.filter(
                  (e, filterIndex) => filterIndex !== index && e.id !== item.id
                );
                // newProductOptions.current.splice(index, 1);
                setnewProductOptions((prev) => {
                  const clone = structuredClone(prev);
                  clone.splice(index, 1);
                  return clone;
                });

                setnewProduct((prevState) => ({
                  ...prevState,
                  options: newProductOptionsUpdated,
                }));
                setindexOn(null);
              }}
            >
              <MaterialCommunityIcons
                name="delete-outline"
                style={styles.deleteIcon}
              />
            </TouchableOpacity>
          </View>
        </TouchableOpacity>
        {indexOn === index && (
          <OptionsItemOpenInner
            item={item}
            newProduct={newProduct}
            newProductOptions={newProductOptions}
            setnewProductOptions={setnewProductOptions}
            index={index}
            e={e}
            sete={sete}
            scrollY={scrollY}
            optionsOnLayout={(height, idx) => updateOptionHeight(height, idx)}
            ifStatementOnLayout={(height, idx) =>
              updateIfStatementHeight(height, idx)
            }
          />
        )}
      </View>
      {index === newProduct.options.length - 1 && (
        <TouchableOpacity
          onPress={() => {
            setnewProductOptions((prev) => {
              const clone = structuredClone(prev);
              clone.push({
                label: null,
                optionsList: [],
                selectedCaseKey: null,
                selectedCaseValue: null,
                numOfSelectable: null,
                id: Math.random().toString(36).substr(2, 9),
                optionType: null,
              });
              return clone;
            });
            setindexOn(newProductOptions.length);
          }}
          disabled={e.label === null}
          style={styles.createOptionBtn}
        >
          <Text style={styles.createOptionTxt}>Create Option</Text>
        </TouchableOpacity>
      )}
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    borderWidth: 1,
    borderColor: "#000000",
    backgroundColor: "rgba(255,255,255,1)",
    justifyContent: "flex-start",
    marginBottom: 30,
  },
  innerOptionContainer1: {
    width: 808,
    height: 389,
    justifyContent: "flex-start",
    alignItems: "flex-start",
    marginTop: 20,
    marginLeft: 20,
  },
  optionMainInfoRow1: {
    width: 808,
    height: 84,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  optionNameInputGroup1: {
    width: 239,
    height: 84,
    justifyContent: "space-between",
  },
  optionNameInputLbl1: {
    color: "#121212",
    fontSize: 17,
  },
  optionNameInput2: {
    width: 239,
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5,
  },
  optionTypeGroup1: {
