import { View, Text, Modal, TouchableOpacity } from "react-native";
import React, { useEffect, useRef, useState } from "react";

//typscript type for props
type Props = {
  children: React.ReactNode;
  step: number;
  helpDescription: string;
  isLastStep?: boolean | undefined;
  stepDetails: number;
  setStepDetails: (prev: { complete: boolean; step: number }) => void;
  changeNextBtnFunc?: () => void;
  alignInfo?: "left" | "right" | "center";
};

const TutorialStep = (props: Props) => {
  const {
    children,
    step,
    helpDescription,
    isLastStep,
    stepDetails,
    setStepDetails,
    changeNextBtnFunc,
    alignInfo,
  } = props;

  // X
  const [x, setX] = useState();

  // Y
  const [y, setY] = useState();

  //DescriptionViewWidth
  const [descriptionViewWidth, setDescriptionViewWidth] = useState();

  return (
    <>
      {step === stepDetails.step && stepDetails.complete === false && (
        <Modal transparent>
          <View
            style={{
              backgroundColor: "rgba(0,0,0,0.5)",
              position: "absolute",
              height: "100%",
              width: "100%",
            }}
          />
          <View style={{ position: "absolute", top: y, left: x }}>
            {children}
          </View>
          <View
            onLayout={(event) => {
              const layout = event.nativeEvent.layout;
              console.log(layout);
              setDescriptionViewWidth(layout.width);
            }}
            style={[
              {
                position: "absolute",
                top: y + 100,
                padding: 20,
                backgroundColor: "white",
              },
              alignInfo === "left" && {
                left: x - descriptionViewWidth / 2 + 20,
              },
              alignInfo === "right" && {
                left: x - descriptionViewWidth / 2 - 20,
              },
              alignInfo === "center" && { left: x - descriptionViewWidth / 2 },
            ]}
          >
            <View
              style={{
                width: 0,
                height: 0,
                backgroundColor: "transparent",
                borderStyle: "solid",
                borderLeftWidth: 15,
                borderRightWidth: 15,
                borderBottomWidth: 15,
                borderLeftColor: "transparent",
                borderRightColor: "transparent",
                borderBottomColor: "white",
                position: "absolute",
                top: -15,
                left: descriptionViewWidth / 2 - 7.5,
              }}
            />
            <Text style={{ marginBottom: 20 }}>{helpDescription}</Text>
            <View
              style={{
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "flex-end",
              }}
            >
              {step > 1 ? (
                <TouchableOpacity
                  onPress={() => {
                    setStepDetails((prev) => ({
                      ...prev,
                      step: prev.step - 1,
                    }));
                    localStorage.setItem(
                      "tutorialComplete",
                      JSON.stringify({
                        ...prev,
                        step: prev.step - 1,
                      })
                    );
                  }}
                >
                  <Text style={{ marginRight: 15 }}>Previous</Text>
                </TouchableOpacity>
              ) : (
                <TouchableOpacity
                  onPress={() => {
                    setStepDetails((prev) => ({
                      ...prev,
                      step: prev.step + 1,
                    }));
                    localStorage.setItem(
                      "tutorialComplete",
                      JSON.stringify({
                        ...prev,
                        step: prev.step + 1,
                      })
                    );
                  }}
                >
                  <Text style={{ marginRight: 15 }}>Skip</Text>
                </TouchableOpacity>
              )}
              {isLastStep ? (
                <TouchableOpacity
                  onPress={() => {
                    setStepDetails((prev) => ({
                      ...prev,
                      step: 0,
                      complete: true,
                    }));
                    localStorage.setItem(
                      "tutorialComplete",
                      JSON.stringify({
                        ...prev,
                        step: 0,
                        complete: true,
                      })
                    );
                  }}
                >
                  <Text>Finish</Text>
                </TouchableOpacity>
              ) : (
                <TouchableOpacity
                  onPress={() => {
                    if (changeNextBtnFunc) {
                      changeNextBtnFunc();
                    } else {
                      setStepDetails((prev) => ({
                        ...prev,
                        step: prev.step + 1,
                      }));
                      localStorage.setItem(
                        "tutorialComplete",
                        JSON.stringify({
                          ...prev,
                          step: prev.step + 1,
                        })
                      );
                    }
                  }}
                >
                  <Text>Next</Text>
                </TouchableOpacity>
              )}
            </View>
          </View>
        </Modal>
      )}
      <View
        onLayout={(event) => {
          const layout = event.nativeEvent.layout;
          console.log(layout);
          setX(layout.left);
          setY(layout.top);
        }}
      >
        {children}
      </View>
    </>
  );
};

export default TutorialStep;
