import React, { useState } from "react";

type GeneralSwitchProps = {
  isActive: boolean;
  setIsActive: React.Dispatch<React.SetStateAction<boolean>>;
  customToggleFunction?: () => void;
};

const GeneralSwitch = ({
  isActive,
  setIsActive,
  customToggleFunction,
}: GeneralSwitchProps) => {
  // Function to toggle switch state
  const toggleSwitch = () => {
    if (customToggleFunction) {
      customToggleFunction();
    } else {
      setIsActive(!isActive);
    }
  };

  // Styles
  const switchStyles = {
    cursor: "pointer",
    width: "39px",
    height: "18px",
    borderRadius: "15px",
    backgroundColor: isActive ? "#1D294E" : "#ccc",
    position: "relative",
    transition: "background-color 0.3s",
  };

  const knobStyles = {
    position: "absolute",
    top: "0px",
    left: isActive ? "21px" : "1px", // Move knob based on isActive
    width: "18px",
    height: "18px",
    borderRadius: "50%",
    backgroundColor: "#fff",
    transition: "left 0.3s",
  };

  return (
    <div style={switchStyles} onClick={toggleSwitch}>
      <div style={knobStyles}></div>
    </div>
  );
};

export default GeneralSwitch;
