import React, { Component } from "react";
import { StyleSheet, TouchableOpacity, Text, Image } from "react-native";

function ActiveCategoryBtn(props) {
  return (
    <TouchableOpacity style={[styles.container, props?.style]}>
      <Text style={styles.categoryLbl}>{props.category ? props.category : 'Placeholder'}</Text>
      <Image
        source={require("../../assets/images/image_HuEF..png")}
        resizeMode="contain"
        style={styles.categoryImg}
      ></Image>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "rgba(29,41,78,1)",
    borderRadius: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  categoryLbl: {
    fontWeight: "700",
    color: "rgba(255,255,255,1)",
    fontSize: 16,
    marginTop: 0,
    marginBottom: 0,
    margin: "null",
  },
  categoryImg: {
    height: 96,
    width: 95,
    marginTop: 0,
    marginBottom: 0,
    margin: "null",
  },
});

export default ActiveCategoryBtn;
