import React, { useEffect, useState } from "react";
import {
  StyleSheet,
  View,
  TouchableOpacity,
  Image,
  Text,
  ScrollView,
  useWindowDimensions,
} from "react-native";
import CategoryBtn from "./components/cartOrder/CategoryBtn";
import ItemContainer from "./components/cartOrder/ItemContainer";
import CartItem from "./components/cartOrder/CartItem";
import UntitledComponent from "./components/cartOrder/UntitledComponent";
import { Entypo, Feather } from "@expo/vector-icons";
import {
  cartState,
  customersList,
  myDeviceDetailsState,
  setCartState,
  storeDetailState,
  userStoreState,
  setCustomersList,
} from "state/state";
import ItemContainerMobile from "./components/cartOrder/ItemContainerMobile";
import Modal from "react-native-modal";
import { useHistory } from "react-router-dom";
import { auth, db } from "state/firebaseConfig";
import ReceiptPrint from "components/functional/ReceiptPrint";

function OrderPagePosHome({ navigation }) {
  const { height, width } = useWindowDimensions();
  const [section, setsection] = useState(null);
  const catalog = userStoreState.use();
  const [cartOpen, setcartOpen] = useState(false);
  const [deliveryModal, setDeliveryModal] = useState(false);
  const [cashModal, setCashModal] = useState(false);
  const [changeModal, setChangeModal] = useState(false);
  const [ongoingDelivery, setOngoingDelivery] = useState(null);
  const [name, setName] = useState(null);
  const [phone, setPhone] = useState(null);
  const [address, setAddress] = useState(null);
  const [buzzCode, setBuzzCode] = useState(null);
  const [unitNumber, setUnitNumber] = useState(null);
  const [deliveryChecked, setDeliveryChecked] = useState(false);
  const [changeDue, setChangeDue] = useState(null);
  const cart = cartState.use();
  const storeDetails = storeDetailState.use();
  const [cartSub, setCartSub] = useState(0);
  const [saveCustomerModal, setSaveCustomerModal] = useState(false);
  const [savedCustomerDetails, setsavedCustomerDetails] = useState(null);
  const [ongoingOrderListModal, setongoingOrderListModal] = useState(false);
  const [settingsPasswordModalVis, setsettingsPasswordModalVis] =
    useState(false);
  const history = useHistory();
  const [updatingOrder, setupdatingOrder] = useState(false);
  const [ongoingListState, setongoingListState] = useState([]);
  const customers = customersList.use();
  const myDeviceDetails = myDeviceDetailsState.use();
  const [clockinModal, setclockinModal] = useState(false);
  const [discountModal, setdiscountModal] = useState(false);

  function parseDate(input) {
    // Check if the input is a Date object
    if (Object.prototype.toString.call(input) === "[object Date]") {
      if (!isNaN(input.getTime())) {
        // It's a valid Date object, return it
        return input;
      }
    }

    // Check if the input is a string
    if (typeof input === "string") {
      const dateObject = new Date(input);

      // Check if the dateObject is a valid Date
      if (!isNaN(dateObject.getTime())) {
        // It's a valid Date object, return it
        return dateObject;
      }
    }
  }

  useEffect(() => {
    const unsub = db
      .collection("users")
      .doc(auth.currentUser?.uid)
      .collection("pendingOrders")
      .onSnapshot((snapshot) => {
        const list = [];
        snapshot.forEach((doc) => {
          list.push({ ...doc.data(), id: doc.id });
          if (
            doc.data().online &&
            !doc.data().printed &&
            myDeviceDetails.printOnlineOrders
          ) {
            console.log("Printing");
            const data = ReceiptPrint(doc.data(), storeDetails);
            const qz = require("qz-tray");
            qz.websocket
              .connect()
              .then(function () {
                const config = qz.configs.create(
                  myDeviceDetails.printToPrinter
                );
                return qz.print(config, data.data);
              })
              .then(qz.websocket.disconnect)
              .catch(function (err) {
                // console.error(err);
                alert(
                  "An error occured while trying to print. Try refreshing the page and trying again."
                );
              });
            db.collection("users")
              .doc(auth.currentUser?.uid)
              .collection("pendingOrders")
              .doc(doc.id)
              .update({
                printed: true,
              });
          }
        });

        const sortedArray = list.sort((a, b) => {
          const dateA = a.online ? parseDate(a.date) : a.date.toDate();
          const dateB = b.online ? parseDate(b.date) : b.date.toDate();

          // Compare dates
          return dateB - dateA;
        });

        setongoingListState(sortedArray);
      });

    return () => unsub();
  }, []);

  useEffect(() => {
    // console.log("cart", cart);
    if (cart.length > 0) {
      let newVal = 0;
      for (let i = 0; i < cart.length; i++) {
        try {
          if (cart[i].quantity > 1) {
            newVal += parseFloat(cart[i].price) * cart[i].quantity;
            // console.log("Cart item quantity ", cart[i].quantity);
          } else {
            newVal += parseFloat(cart[i].price);
          }
        } catch (error) {
          console.log(error);
        }
      }
      if (deliveryChecked) {
        setCartSub(newVal + parseFloat(storeDetails.deliveryPrice));
      } else {
        setCartSub(newVal);
      }
      //check if there is a discount in the cart then move it the end of the cart
      if (cart.length > 1) {
        if (!cart[cart.length - 1].name.includes("Cart Discount")) {
          for (let i = 0; i < cart.length; i++) {
            if (cart[i].name.includes("Cart Discount")) {
              console.log("In first if");
              const discountItem = cart[i];
              const newCart = structuredClone(cart);
              newCart.splice(i, 1);

              if (discountItem.percent) {
                newCart.push({
                  ...discountItem,
                  price:
                    (newVal + -discountItem.price) * discountItem.percent * -1,
                });
              } else {
                newCart.push(discountItem);
              }
              setCartState(newCart);
            }
          }
        } else if (cart[cart.length - 1].percent) {
          console.log("In second if");
          const discountItem = cart[cart.length - 1];
          if (
            (newVal + -discountItem.price) * discountItem.percent * -1 !==
            discountItem.price
          ) {
            const newCart = structuredClone(cart);
            newCart.pop();
            newCart.push({
              ...discountItem,
              price: (newVal + -discountItem.price) * discountItem.percent * -1,
            });
            setCartState(newCart);
          }
        }
      }
    } else {
      setCartSub(0);
    }
  }, [cart, deliveryChecked]);

  // const AddToList = async (payload) => {
  //   updateTransList(payload);
  // };

  const Print = (method, dontAddToOngoing) => {
    if (!myDeviceDetails.id) {
      return alert("Please set up a device in Settings -> Devices");
    }

    if (savedCustomerDetails) {
      if (savedCustomerDetails.orders?.length > 0) {
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("customers")
          .doc(savedCustomerDetails.id)
          .update({
            orders: [...savedCustomerDetails.orders, { cart }],
          });
        const indexOfCustomer = customers.findIndex(
          (customer) => customer.id === savedCustomerDetails.id
        );
        const newCustomers = structuredClone(customers);
        newCustomers[indexOfCustomer].orders.push({ cart });
        setCustomersList(newCustomers);
      } else {
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("customers")
          .doc(savedCustomerDetails.id)
          .update({
            orders: [{ cart }],
          });
        const indexOfCustomer = customers.findIndex(
          (customer) => customer.id === savedCustomerDetails.id
        );
        const newCustomers = structuredClone(customers);
        newCustomers[indexOfCustomer].orders = [{ cart }];
        setCustomersList(newCustomers);
      }
    }

    const transNum = Math.random().toString(36).substr(2, 9);

    if (method === "deliveryOrder") {
      const today = new Date();

      const element = {
        date: today,
        transNum: transNum,
        method: "deliveryOrder",
        cart: cart,
        customer: {
          name: name,
          phone: phone,
          address: address,
          buzzCode: buzzCode,
          unitNumber: unitNumber,
        },
      };

      const data = ReceiptPrint(element, storeDetails);

      if (!dontAddToOngoing) {
        console.log("Adding to pending orders");
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("pendingOrders")
          .add({
            date: today,
            transNum: transNum,
            method: "deliveryOrder",
            cart: cart,
            total: data.total,
            customer: {
              name: name,
              phone: phone,
              address: address ? address : null,
              buzzCode: buzzCode,
              unitNumber: unitNumber,
            },
          });
      }

      if (
        myDeviceDetails.sendPrintToUserID &&
        myDeviceDetails.useDifferentDeviceToPrint
      ) {
        console.log("Sending print to different user");
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("devices")
          .doc(myDeviceDetails.sendPrintToUserID.value)
          .collection("printRequests")
          .add({
            printData: data.data,
          });
      } else {
        const qz = require("qz-tray");
        qz.websocket
          .connect()
          .then(function () {
            const config = qz.configs.create(myDeviceDetails.printToPrinter);
            return qz.print(config, data.data);
          })
          .then(qz.websocket.disconnect)
          .catch(function (err) {
            // console.error(err);
            alert(
              "An error occured while trying to print. Try refreshing the page and trying again."
            );
          });
      }

      setCartState([]);
      setDeliveryModal(false);
    } else if (method === "pickupOrder") {
      const today = new Date();

      const element = {
        date: today,
        transNum: transNum,
        method: "pickupOrder",
        cart: cart,
        customer: {
          name: name,
          phone: phone,
          // address: address,
        },
      };

      const data = ReceiptPrint(element, storeDetails);

      if (!dontAddToOngoing) {
        console.log("Adding to pending orders");
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("pendingOrders")
          .add({
            date: today,
            transNum: transNum,
            method: "pickupOrder",
            cart: cart,
            total: data.total,
            customer: {
              name: name,
              phone: phone,
              address: address ? address : "null",
            },
          });
      }

      if (
        myDeviceDetails.sendPrintToUserID &&
        myDeviceDetails.useDifferentDeviceToPrint
      ) {
        console.log("Sending print to different user");
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("devices")
          .doc(myDeviceDetails.sendPrintToUserID.value)
          .collection("printRequests")
          .add({
            printData: data.data,
          });
      } else {
        const qz = require("qz-tray");
        qz.websocket
          .connect()
          .then(function () {
            const config = qz.configs.create(myDeviceDetails.printToPrinter);
            return qz.print(config, data.data);
          })
          .then(qz.websocket.disconnect)
          .catch(function (err) {
            // console.error(err);
            alert(
              "An error occured while trying to print. Try refreshing the page and trying again."
            );
          });
      }

      setCartState([]);
      setDeliveryModal(false);
      setName(null);
      setPhone(null);
      setAddress(null);
      setDeliveryChecked(false);
    } else {
      const today = new Date();
      const element = {
        date: today,
        transNum: transNum,
        method: "inStoreOrder",
        cart: cart,
        customer: {
          name: name,
          phone: phone,
          address: address,
        },
        changeDue: changeDue,
        paymentMethod: method,
      };

      const data = ReceiptPrint(element, storeDetails);

      db.collection("users")
        .doc(auth.currentUser?.uid)
        .collection("pendingOrders")
        .add({
          date: today,
          transNum: transNum,
          total: data.total,
          method: "inStoreOrder",
          paymentMethod: method,
          cart: cart,
        });
      if (
        myDeviceDetails.sendPrintToUserID &&
        myDeviceDetails.useDifferentDeviceToPrint
      ) {
        console.log("Sending print to different user");
        db.collection("users")
          .doc(auth.currentUser?.uid)
          .collection("devices")
          .doc(myDeviceDetails.sendPrintToUserID.value)
          .collection("printRequests")
          .add({
            printData: data.data,
          });
      } else {
        const qz = require("qz-tray");
        qz.websocket
          .connect()
          .then(function () {
            const config = qz.configs.create(myDeviceDetails.printToPrinter);
            return qz.print(config, data.data);
          })
          .then(qz.websocket.disconnect)
          .catch(function (err) {
            // console.error(err);
            alert(
              "An error occured while trying to print. Try refreshing the page and trying again."
            );
          });
      }
    }

    setCartState([]);
    setName(null);
    setPhone(null);
    setAddress(null);
    setDeliveryChecked(false);
    setChangeDue(null);
  };

  useEffect(() => {
    if (cart.length > 0) {
      let newVal = 0;
      for (let i = 0; i < cart.length; i++) {
        try {
          if (cart[i].quantity > 1) {
            newVal += parseFloat(cart[i].price) * cart[i].quantity;
            // console.log("Cart item quantity ", cart[i].quantity);
          } else {
            newVal += parseFloat(cart[i].price);
          }
        } catch (error) {
          console.log(error);
        }
      }
      if (orderDetails.delivery) {
        setCartSub(newVal + parseFloat(storeDetails.deliveryPrice));
      } else {
        setCartSub(newVal);
      }
    } else {
      setCartSub(0);
    }
  }, [cart]);

  useEffect(() => {
    catalog.products.map((product, index) => {
      if (product.category === section) {
        document.getElementById(product.id).style.display = "flex";
      } else {
        document.getElementById(product.id).style.display = "none";
      }
    });
  }, [section]);

  const CheckoutBtn = () => {
    if (updatingOrder) {
      return (
        <TouchableOpacity
          style={[styles.bigButton, cartSub === 0 && { opacity: 0.5 }]}
          disabled={cart.length < 1}
          onPress={() => {
            // const oldList = structuredClone(
            //   JSON.parse(localStorage.getItem("ongoingList"))
            // );
            // oldList[updatingOrder.index].cart = cart;
            // setongoingListState(oldList);
            db.collection("users")
              .doc(auth.currentUser?.uid)
              .collection("pendingOrders")
              .doc(updatingOrder.id)
              .delete();

            Print(deliveryChecked ? "deliveryOrder" : "pickupOrder", false);
            setOngoingDelivery(null);
            setName(null);
            setPhone(null);
            setAddress(null);
            setDeliveryChecked(false);
            setupdatingOrder(false);
          }}
        >
          <Text style={styles.btnTxt}>Update Order</Text>
        </TouchableOpacity>
      );
    }

    if (ongoingDelivery === null) {
      return (
        <View
          style={{
            flexDirection: "row",
            width: "100%",
            alignSelf: "center",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: 10,
          }}
        >
          <TouchableOpacity
            style={[styles.cashButton, cartSub === 0 && { opacity: 0.5 }]}
            onPress={() => setCashModal(true)}
            disabled={cart.length < 1 || ongoingDelivery}
          >
            <Text style={styles.btnTxt}>Cash</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={[styles.cardButton, cartSub === 0 && { opacity: 0.5 }]}
            onPress={() => {
              Print("Card");
            }}
            disabled={cart.length < 1 || ongoingDelivery}
          >
            <Text style={styles.btnTxt}>Card</Text>
          </TouchableOpacity>
        </View>
      );
    }
    if (ongoingDelivery && cart.length > 0) {
      return (
        <TouchableOpacity
          style={styles.bigButton}
          onPress={() => {
            Print(deliveryChecked ? "deliveryOrder" : "pickupOrder", false);
            setOngoingDelivery(null);
            setName(null);
            setPhone(null);
            setAddress(null);
          }}
        >
          <Text style={styles.btnTxt}>Complete</Text>
        </TouchableOpacity>
      );
    } else {
      return (
        <TouchableOpacity
          style={styles.bigButton}
          onPress={() => {
            setOngoingDelivery(null);
            setDeliveryChecked(false);
            setName(null);
            setPhone(null);
            setAddress(null);
            setChangeDue(null);
          }}
        >
          <Text style={styles.btnTxt}>Cancel</Text>
        </TouchableOpacity>
      );
    }
  };

  return (
    <View style={styles.container}>
      {width > 1250 && (
        <View style={styles.leftMenuBarContainer}>
          <TouchableOpacity style={styles.menuBtn}>
            <Entypo name="menu" style={styles.menuIcon}></Entypo>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => setpage(1)}>
            <Feather name="log-out" style={styles.icon}></Feather>
          </TouchableOpacity>
        </View>
      )}
      <View
        style={[
          styles.menuContainer,
          width < 1250 && { width: "62%" },
          width < 1000 && { width: "100%" },
        ]}
      >
        {width < 1000 && (
          <View
            style={{
              flexDirection: "row",
              width: "88%",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: 10,
              marginTop: 10,
            }}
          >
            <TouchableOpacity
              onPress={() => {
                // setpage(1);
              }}
              style={{
                backgroundColor: "#1D294E",
                borderRadius: 10,
                justifyContent: "center",
                alignItems: "center",
                width: 34,
                height: 34,
              }}
            >
              <Feather name="log-out" style={{ color: "white" }} size={20} />
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => {
                setcartOpen(true);
              }}
              style={{
                backgroundColor: "#1D294E",
                borderRadius: 10,
                justifyContent: "space-between",
                alignItems: "center",
                width: 58,
                height: 34,
                flexDirection: "row",
                padding: 5,
              }}
            >
              <Feather
                name="shopping-cart"
                style={{ color: "white" }}
                size={20}
              />
              <Text style={{ color: "white", fontSize: 20 }}>
                {cart.length}
              </Text>
            </TouchableOpacity>
          </View>
        )}
        {width > 1000 && (
          <View
            style={[
              styles.bannerContainer,
              width < 1000 && {
                height: 100,
                justifyContent: "center",
                alignItems: "center",
              },
            ]}
          >
            <Image
              source={require("./assets/images/image_onW3..png")}
              resizeMode="contain"
              style={[
                styles.logo,
                width < 1000 && { height: 60, maxWidth: 250 },
              ]}
            ></Image>
          </View>
        )}
        <View style={styles.categoryContainer}>
          <Text style={styles.lblTxt}>Menu Category</Text>
          <View style={styles.scrollArea}>
            <ScrollView
              horizontal={true}
              contentContainerStyle={styles.scrollArea_contentContainerStyle}
            >
              {catalog.categories?.map((category, index) => {
                if (
                  catalog.products.filter(
                    (x) => x.category === category && x.hasImage
                  ).length > 0
                ) {
                  return (
                    <CategoryBtn
                      key={index}
                      category={category}
                      onPress={() => {
                        setsection(category);
                      }}
                      isSelected={section === category}
                      style={styles.activeCategoryBtn}
                      imageUrl={
                        catalog.products[
                          catalog.products.findIndex(
                            (x) => x.category === category && x.hasImage
                          )
                        ]?.imageUrl
                      }
                    />
                  );
                } else {
                  return (
                    <CategoryBtn
                      key={index}
                      category={category}
                      onPress={() => {
                        setsection(category);
                      }}
                      isSelected={section === category}
                      style={styles.categoryBtn}
                      imageUrl={null}
                    />
                  );
                }
              })}
            </ScrollView>
          </View>
        </View>
        <View style={styles.scrollAreaProducts}>
          <ScrollView
            contentContainerStyle={
              styles.scrollAreaProducts_contentContainerStyle
            }
          >
            {catalog.products.map((product, index) =>
              width > 1250 ? (
                <ItemContainer
                  product={product}
                  productIndex={index}
                  key={index}
                  userUid={catalog.docID}
                  style={styles.itemContainer}
                />
              ) : (
                <ItemContainerMobile
                  product={product}
                  productIndex={index}
                  key={index}
                  userUid={catalog.docID}
                  style={{
                    height: 220,
                    width: 160, // Ensure this width accounts for any margins or padding
                    marginBottom: 30,
                  }}
                  setshowProduct={setshowProduct}
                />
              )
            )}
          </ScrollView>
        </View>
      </View>
      {width > 1000 ? (
        <View style={[styles.cartContainer, width < 1250 && { width: "38%" }]}>
          <Text style={styles.myCartTxt}>My Cart</Text>
          {cart.length > 0 ? (
            <View style={styles.cartItems}>
              <ScrollView
                horizontal={false}
                contentContainerStyle={styles.cartItems_contentContainerStyle}
              >
                {cart?.map((cartItem, index) => (
                  <CartItem
                    style={styles.cartItem1}
                    key={index}
                    cartItem={cartItem}
                    index={index}
                    removeAction={() => {
                      console.log("Removing");
                      const local = structuredClone(cart);
                      local.splice(index, 1);
                      setCartState(local);
                    }}
                    decreaseAction={() => {
                      const local = structuredClone(cart);
                      local[index].quantity--;
                      setCartState(local);
                    }}
                    increaseAction={() => {
                      const local = structuredClone(cart);
                      if (local[index].quantity) {
                        local[index].quantity++;
                      } else {
                        local[index].quantity = 2;
                      }
                      setCartState(local);
                    }}
                  />
                ))}
              </ScrollView>
            </View>
          ) : (
            <Image
              source={require("./assets/images/noItemsImg.png")}
              style={{ width: 200, height: "35%", resizeMode: "contain" }}
            />
          )}
          <View style={styles.totalsContainer}>
            <View style={styles.topGroupTotalsContainer}>
              <UntitledComponent
                amountValue="N/A"
                amountLbl="Discount"
                style={styles.discountRow}
              />
              {deliveryChecked &&
                parseFloat(storeDetails.deliveryPrice) && (
                  <UntitledComponent
                    amountValue={`$${parseFloat(
                      storeDetails.deliveryPrice
                    ).toFixed(2)}`}
                    amountLbl="Delivery"
                    style={styles.discountRow}
                  />
                )}
              <UntitledComponent
                amountValue={
                  deliveryChecked &&
                  parseFloat(storeDetails.deliveryPrice) &&
                  cartSub > 0
                    ? `$${(
                        cartSub - parseFloat(storeDetails.deliveryPrice)
                      ).toFixed(2)}`
                    : `$${cartSub.toFixed(2)}`
                }
                amountLbl="Subtotal"
                style={styles.subtotalRow}
              />
              <UntitledComponent
                amountValue={`$${(
                  cartSub *
                  (storeDetails.taxRate ? storeDetails.taxRate / 100 : 0.13)
                ).toFixed(2)}`}
                amountLbl="Tax"
                style={styles.taxRow}
              />
            </View>
            <View style={styles.totalRowGroup}>
              <View style={styles.totalRow}>
                <Text style={styles.total2}>Total</Text>
                <Text style={styles.totalValue}>
                  $
                  {(
                    Math.ceil(
                      cartSub *
                        (storeDetails.taxRate
                          ? 1 + storeDetails.taxRate / 100
                          : 1.13) *
                        10
                    ) / 10
                  ).toFixed(2)}
                </Text>
              </View>
              <TouchableOpacity style={styles.discountCodeBtn}>
                <Text style={styles.discountCode}>Discount Code</Text>
              </TouchableOpacity>
            </View>
          </View>
          <CheckoutBtn />
        </View>
      ) : (
        <Modal
          isVisible={cartOpen}
          animationIn="slideInUp"
          animationOut="slideOutDown"
          style={{ margin: 0 }}
        >
          <View
            style={{
              width: width,
              height: height,
              justifyContent: "space-evenly",
              alignItems: "center",
              backgroundColor: "white",
