                />
              </TouchableOpacity>
              <View
                style={{
                  backgroundColor: "#1D294E",
                  borderRadius: 10,
                  justifyContent: "space-between",
                  alignItems: "center",
                  width: 80,
                  height: 40,
                  flexDirection: "row",
                  padding: 15,
                }}
              >
                <Feather
                  name="shopping-cart"
                  style={{ color: "white" }}
                  size={22}
                />
                <Text style={{ color: "white", fontSize: 20 }}>
                  {cart.length}
                </Text>
              </View>
            </View>
            <Text style={styles.myCartTxt}>My Cart</Text>
            {cart.length > 0 ? (
              <View style={styles.cartItems}>
                <ScrollView
                  horizontal={false}
                  contentContainerStyle={styles.cartItems_contentContainerStyle}
                >
                  {cart?.map((cartItem, index) => (
                    <CartItem
                      style={styles.cartItem1}
                      key={index}
                      cartItem={cartItem}
                      index={index}
                      removeAction={() => {
                        console.log("Removing");
                        const local = structuredClone(cart);
                        local.splice(index, 1);
                        setCartState(local);
                      }}
                      decreaseAction={() => {
                        const local = structuredClone(cart);
                        local[index].quantity--;
                        setCartState(local);
                      }}
                      increaseAction={() => {
                        const local = structuredClone(cart);
                        if (local[index].quantity) {
                          local[index].quantity++;
                        } else {
                          local[index].quantity = 2;
                        }
                        setCartState(local);
                      }}
                    />
                  ))}
                </ScrollView>
              </View>
            ) : (
              <Image
                source={require("./assets/images/noItemsImg.png")}
                style={{ width: 200, height: "35%", resizeMode: "contain" }}
              />
            )}
            <View style={[styles.totalsContainer, { height: 150 }]}>
              <View style={styles.topGroupTotalsContainer}>
                {orderDetails.delivery &&
                  parseFloat(storeDetails.deliveryPrice) && (
                    <UntitledComponent
                      amountValue={`$${parseFloat(
                        storeDetails.deliveryPrice
                      ).toFixed(2)}`}
                      amountLbl="Delivery"
                      style={styles.discountRow}
                    />
                  )}
                <UntitledComponent
                  amountValue={
                    orderDetails.delivery &&
                    parseFloat(storeDetails.deliveryPrice) &&
                    cartSub > 0
                      ? `$${(
                          cartSub - parseFloat(storeDetails.deliveryPrice)
                        ).toFixed(2)}`
                      : `$${cartSub.toFixed(2)}`
                  }
                  amountLbl="Subtotal"
                  style={styles.subtotalRow}
                />
                <UntitledComponent
                  amountValue={`$${(
                    cartSub *
                    (storeDetails.taxRate ? storeDetails.taxRate / 100 : 0.13)
                  ).toFixed(2)}`}
                  amountLbl="Tax"
                  style={styles.taxRow}
                />
                <View style={styles.totalRow}>
                  <Text style={styles.total2}>Total</Text>
                  <Text style={styles.totalValue}>
                    $
                    {(
                      Math.ceil(
                        cartSub *
                          (storeDetails.taxRate
                            ? 1 + storeDetails.taxRate / 100
                            : 1.13) *
                          10
                      ) / 10
                    ).toFixed(2)}
                  </Text>
                </View>
              </View>
            </View>
            <TouchableOpacity
              style={[styles.checkoutBtn, { margin: 20 }]}
              disabled={cart.length < 1}
              onPress={() => {
                const today = new Date();
                const transNum = Math.random().toString(36).substr(2, 9);

                if (orderDetails.delivery) {
                  setorderDetails((prev) => ({
                    date: today,
                    transNum: transNum,
                    total: (
                      cartSub *
                      (storeDetails.taxRate
                        ? 1 + storeDetails.taxRate / 100
                        : 1.13)
                    ).toFixed(2),
                    method: "deliveryOrder",
                    online: true,
                    cart: cart,
                    customer: {
                      name: prev.customer.name,
                      phone: prev.customer.phone,
                      address: prev.customer.address,
                      email: prev.customer.email,
                    },
                    address: prev.address,
                  }));
                  setcartOpen(false);
                  setpage(5);
                } else {
                  setorderDetails((prev) => ({
                    date: today,
                    transNum: transNum,
                    total: (
                      cartSub *
                      (storeDetails.taxRate
                        ? 1 + storeDetails.taxRate / 100
                        : 1.13)
                    ).toFixed(2),
                    method: "pickupOrder",
                    online: true,
                    cart: cart,
                    customer: {
                      name: prev.customer.name,
                      phone: prev.customer.phone,
                      email: prev.customer.email,
                      address: null,
                    },
                  }));
                }
                setcartOpen(false);
                setpage(5);
              }}
            >
              <Text style={styles.checkoutLbl}>Checkout</Text>
            </TouchableOpacity>
          </View>
        </Modal>
      )}
    </View>
  );
}

