import React, { Component, useState } from "react";
import { FontAwesome, Entypo } from "@expo/vector-icons";
import DeliveryDetails from "./components/home/DeliveryDetails";
import PickupDetails from "./components/home/PickupDetails";
import {
  Text,
  TouchableOpacity,
  View,
  useWindowDimensions,
  Image,
  StyleSheet,
} from "react-native";
import CheckOutDetails from "./components/home/CheckOutDetails";
import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";

function OnlineOrderHome({
  storeDetails,
  setorderDetails,
  orderDetails,
  setpage,
  page,
}) {
  const [enterDetailsSection, setenterDetailsSection] = useState(
    page === 3 ? "checkout" : null
  );
  const screenWidth = useWindowDimensions().width;
  const screenHeight = useWindowDimensions().height;

  const RenderDetailsSection = () => {
    if (enterDetailsSection === "delivery") {
      return (
        <DeliveryDetails
          storeDetails={storeDetails}
          setorderDetails={setorderDetails}
          orderDetails={orderDetails}
          setpage={setpage}
          width={screenWidth > 1000 ? 380 : screenWidth * 0.9}
        />
      );
    } else if (enterDetailsSection === "pickup") {
      return (
        <PickupDetails
          storeDetails={storeDetails}
          setorderDetails={setorderDetails}
          orderDetails={orderDetails}
          setpage={setpage}
        />
      );
    } else if (page === 3) {
      return (
        <Elements stripe={loadStripe(storeDetails.stripePublicKey)}>
          <CheckOutDetails
            storeDetails={storeDetails}
            setorderDetails={setorderDetails}
            orderDetails={orderDetails}
            setpage={setpage}
          />
        </Elements>
      );
    } else {
      return (
        <Text style={{ fontSize: 35, fontWeight: "700", color: "white" }}>
          Thank you for placing a order.
        </Text>
      );
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.backgroundContainer}>
        <View style={styles.plantImgContainer}>
          <Image
            source={require("./assets/images/image_JqcD..png")}
            resizeMode="contain"
            style={[styles.plantImg, screenWidth < 1000 && { width: 100 }]}
          />
          <View style={styles.wingImgContainer}>
            <Image
              source={
                screenWidth > 1000
                  ? require("./assets/images/image_BSgk..png")
                  : require("./assets/images/sidewings.png")
              }
              resizeMode="contain"
              style={[
                styles.wingImg,
                screenWidth < 1000 && {
                  height: 200,
                  position: "absolute",
                  right: 0,
                  bottom: "20%",
                },
              ]}
            />
            <View style={styles.pizzaImgContainer}>
              <Image
                source={require("./assets/images/image_DrUG..png")}
                resizeMode="contain"
                style={[
                  styles.pizzaImg,
                  screenWidth < 1000 && {
                    height: 400,
                    width: 200,
                    right: 0,
                    top: "2%",
                    position: "absolute",
                  },
                ]}
              />
              <View style={styles.frontContainer}>
                <View style={styles.logoGroup}>
                  {storeDetails.hasLogo ? (
                    <TouchableOpacity
                      onPress={() => {
                        if (page > 1) {
                          setpage((prev) => prev - 1);
                        } else {
                          setorderDetails({
                            ...orderDetails,
                            delivery: false,
                            address: null,
                          });
                          setenterDetailsSection(null);
                        }
                      }}
                    >
                      <Image
                        source={require("./assets/images/dpos-logo-white.png")}
                        resizeMode="contain"
                        style={styles.logo}
                      />
                    </TouchableOpacity>
                  ) : (
                    <TouchableOpacity
                      onPress={() => {
                        if (page > 1) {
                          setpage((prev) => prev - 1);
                        } else {
                          setorderDetails({
                            ...orderDetails,
                            delivery: false,
                            address: null,
                          });
                          setenterDetailsSection(null);
                        }
                      }}
                    >
                      <Text
                        style={[
                          { fontSize: 35, fontWeight: "700", color: "white" },
                          screenWidth < 1000 && { fontSize: 30 },
                        ]}
                      >
                        {storeDetails.name}
                      </Text>
                    </TouchableOpacity>
                  )}
                  <Image
                    source={require("./assets/images/image_ridw..png")}
                    resizeMode="contain"
                    style={styles.dash}
                  ></Image>
                </View>
                {/* <View style={styles.btnContainerRow}>
                  <TouchableOpacity style={styles.pickupBtn}>
                    <Text style={styles.pickupBtnTxt}>PICK UP</Text>
                  </TouchableOpacity>
                  <TouchableOpacity style={styles.deliveryBtn}>
                    <Text style={styles.deliveryBtnTxt}>DELIVERY</Text>
                  </TouchableOpacity>
                </View> */}
                {!enterDetailsSection ? (
                  <>
                    {screenWidth > 1000 ? (
                      <>
                        {storeDetails.acceptDelivery ? (
                          <View style={styles.btnContainerRow}>
                            <TouchableOpacity
                              style={styles.pickupBtn}
                              onPress={() => setenterDetailsSection("pickup")}
                            >
                              <Text style={styles.pickupBtnTxt}>PICK UP</Text>
                            </TouchableOpacity>
                            <TouchableOpacity
                              style={styles.deliveryBtn}
                              onPress={() => {
                                setorderDetails({
                                  ...orderDetails,
                                  delivery: true,
                                });
                                setenterDetailsSection("delivery");
                              }}
                            >
                              <Text style={styles.deliveryBtnTxt}>
                                DELIVERY
                              </Text>
                            </TouchableOpacity>
                          </View>
                        ) : (
                          <div
                            style={{
                              width: "100%",
                              display: "flex",
                              justifyContent: "center",
                              alignItems: "center",
                            }}
                          >
                            <TouchableOpacity
                              style={styles.pickupBtn}
                              onPress={() => setenterDetailsSection("pickup")}
                            >
                              <Text style={styles.pickupBtnTxt}>PICK UP</Text>
                            </TouchableOpacity>
                          </div>
                        )}
                      </>
                    ) : (
                      <>
                        {storeDetails.acceptDelivery ? (
                          <View>
                            <TouchableOpacity
                              style={[styles.pickupBtn, { marginBottom: 20 }]}
                              onPress={() => setenterDetailsSection("pickup")}
                            >
                              <Text style={styles.pickupBtnTxt}>PICK UP</Text>
                            </TouchableOpacity>
                            <TouchableOpacity
                              style={styles.deliveryBtn}
                              onPress={() => {
                                setorderDetails({
                                  ...orderDetails,
                                  delivery: true,
                                });
                                setenterDetailsSection("delivery");
                              }}
                            >
                              <Text style={styles.deliveryBtnTxt}>
                                DELIVERY
                              </Text>
                            </TouchableOpacity>
                          </View>
                        ) : (
                          <div
                            style={{
                              width: "100%",
                              display: "flex",
                              justifyContent: "center",
                              alignItems: "center",
                            }}
                          >
                            <TouchableOpacity
                              style={styles.pickupBtn}
                              onPress={() => setenterDetailsSection("pickup")}
                            >
                              <Text style={styles.pickupBtnTxt}>PICK UP</Text>
                            </TouchableOpacity>
                          </div>
                        )}
                      </>
                    )}
                  </>
                ) : (
                  <>
                    {screenWidth > 1000 ? (
                      <RenderDetailsSection />
                    ) : (
                      <View
                        style={{
                          justifyContent: "center",
                          alignItems: "center",
                          width: "100%",
                        }}
                      >
                        <RenderDetailsSection />
                      </View>
                    )}
                  </>
                )}
                {/*  */}
                <View style={styles.bottomRowGroup}>
                  <View style={styles.detailsLocationGroup}>
                    <View style={styles.phoneNumberRow}>
                      <FontAwesome
                        name="phone"
                        style={styles.phoneNumberIcon}
                      ></FontAwesome>
                      <Text style={styles.phoneNumberTxt}>
                        {storeDetails.phoneNumber}
                      </Text>
                    </View>
                    <View style={styles.addressRow}>
                      <Entypo
                        name="location-pin"
                        style={styles.addressIcon}
                      ></Entypo>
                      <Text style={styles.addressTxt}>
                        {
                          storeDetails.address?.value.structured_formatting
                            .main_text
                        }
                        {"\n"}
                        {
                          storeDetails.address?.value.structured_formatting
                            .secondary_text
                        }
                      </Text>
                    </View>
                  </View>
                  {storeDetails.hasSocial && (
                    <View style={styles.socialIconsGroup}>
                      <Image
                        source={require("./assets/images/image_pDaA..png")}
                        resizeMode="contain"
                        style={styles.facebookIcon}
                      ></Image>
                      <Image
                        source={require("./assets/images/image_CLpi..png")}
                        resizeMode="contain"
                        style={styles.instagramIcon}
                      ></Image>
                    </View>
                  )}
                </View>
              </View>
            </View>
          </View>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "rgba(30,30,30,1)",
  },
  backgroundContainer: {
    flex: 1,
  },
  plantImgContainer: {
    alignItems: "flex-start",
    justifyContent: "flex-end",
    flex: 1,
  },
  plantImg: {
    height: 520,
    width: 200,
  },
  wingImgContainer: {
    top: 0,
    left: 0,
    position: "absolute",
    right: 0,
    bottom: 0,
    alignItems: "center",
    justifyContent: "flex-end",
  },
  wingImg: {
    height: 200,
    width: '50%',
  },
  pizzaImgContainer: {
    top: 0,
    left: 0,
    position: "absolute",
    right: 0,
    bottom: 0,
    flexDirection: "row",
    justifyContent: "flex-end",
    alignItems: "flex-end",
  },
  pizzaImg: {
    height: 1000,
    width: 401,
  },
  frontContainer: {
    flex: 1,
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    justifyContent: "space-between",
    alignItems: "center",
  },
