import {
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import React, { useState } from "react";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import useWindowDimensions from "./useWindowDimensions";
import { addCartState, cartState, setCartState } from "state/state";

const DiscountModal = ({ setdiscountModal, cartSub, cart }) => {
  const { height, width } = useWindowDimensions();
  const [discountAmount, setdiscountAmount] = useState("");
  const [code, setcode] = useState("");

  return (
    <>
      <TouchableOpacity
        onPress={() => setdiscountModal(false)}
        style={{
          backgroundColor: "rgba(0, 0, 0, 0.3)",
          justifyContent: "center",
          alignItems: "center",
          height: height,
          width: width,
        }}
      />
      <View
        style={{
          position: "absolute",
          backgroundColor: "rgba(255,255,255,1)",
          borderRadius: 30,
          shadowColor: "rgba(0,0,0,1)",
          shadowOffset: {
            width: 3,
            height: 3,
          },
          elevation: 30,
          shadowOpacity: 0.57,
          shadowRadius: 10,
          height: height * 0.7,
          width: height * 0.7,
          padding: 20,
          alignSelf: "center",
          top: "15%",
        }}
      >
        <View>
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons
              onPress={() => setdiscountModal(false)}
              name="close"
              size={26}
              color="rgba(74,74,74,1)"
              style={{ width: "25%", marginLeft: 20 }}
            />
            <Text
              style={{
                margin: 25,
                fontSize: 20,
                fontWeight: "600",
                width: "50%",
                textAlign: "center",
              }}
            >
              Apply Discount
            </Text>
            <View style={{ width: "25%", marginBottom: 20 }} />
          </View>
          <View style={{ margin: 15 }}>
            <TextInput
              placeholder="Enter Custom Amount or Percentage"
              style={{
                height: 40,
                borderColor: "gray",
                borderWidth: 1,
                borderRadius: 10,
                width: "100%",
                padding: 10,
                marginBottom: 20,
              }}
              onChangeText={(text) => setdiscountAmount(text)}
            />
            <View
              style={{
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              <TouchableOpacity
                style={[
                  {
                    borderRadius: 10,
                    padding: 10,
                  },
                  { backgroundColor: discountAmount === "5%" ? "red" : "grey" },
                ]}
                onPress={() => setdiscountAmount("5%")}
              >
                <Text>5%</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[
                  {
                    borderRadius: 10,
                    padding: 10,
                  },
                  {
                    backgroundColor: discountAmount === "10%" ? "red" : "grey",
                  },
                ]}
                onPress={() => setdiscountAmount("10%")}
              >
                <Text>10%</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[
                  {
                    borderRadius: 10,
                    padding: 10,
                  },
                  {
                    backgroundColor: discountAmount === "15%" ? "red" : "grey",
                  },
                ]}
                onPress={() => setdiscountAmount("15%")}
              >
                <Text>15%</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[
                  {
                    borderRadius: 10,
                    padding: 10,
                  },
                  {
                    backgroundColor: discountAmount === "20%" ? "red" : "grey",
                  },
                ]}
                onPress={() => setdiscountAmount("20%")}
              >
                <Text>20%</Text>
              </TouchableOpacity>
            </View>
            <TextInput
              placeholder="Enter Code"
              style={{
                height: 40,
                borderColor: "gray",
                borderWidth: 1,
                borderRadius: 10,
                width: "100%",
                padding: 10,
                marginBottom: 20,
                marginTop: 15,
              }}
              onChangeText={(text) => setcode(text)}
            />
            <TouchableOpacity
              style={{
                backgroundColor: "rgba(0, 0, 0, 0.1)",
                borderRadius: 10,
                padding: 10,
              }}
              onPress={() => {
                if (code === "10OFF" || !code) {
                  //if discount containts % then it is percentage
                  if (discountAmount.includes("%")) {
                    const discount =
                      parseFloat(discountAmount.replace("%", "")) / 100;
                    const isExistingDiscount =
                      cart.filter((item) => {
                        return item.name.includes("Cart Discount");
                      }).length > 0;
                    if (cart.length > 1 && isExistingDiscount) {
                      const newCart = structuredClone(cart);
                      newCart.map((item, index) => {
                        if (item.name.includes("Cart Discount")) {
                          newCart.splice(index, 1);
                        }
                      });

                      newCart.push({
                        name: "Cart Discount: " + discount * 100 + "%",
                        price: -(cartSub * discount),
                        description: "Discount Applied to Cart",
                        options: [],
                        extraDetails: null,
                      });
                      setCartState(newCart);
                    } else {
                      addCartState(
                        {
                          name: "Cart Discount: " + discount * 100 + "%",
                          price: -(cartSub * discount),
                          description: "Discount Applied to Cart",
                          options: [],
                          extraDetails: null,
                        },
                        cart
                      );
                    }
                  } else {
                    const isExistingDiscount =
                      cart.filter((item) => {
                        return item.name.includes("Cart Discount");
                      }).length > 0;
                    if (cart.length > 1 && isExistingDiscount) {
                      const newCart = structuredClone(cart);
                      newCart.map((item, index) => {
                        if (item.name.includes("Cart Discount")) {
                          newCart.splice(index, 1);
                        }
                      });
                      newCart.push({
                        name: "Cart Discount: $" + discountAmount,
                        price: -parseFloat(discountAmount),
                        description: "Discount Applied to Cart",
                        options: [],
                        extraDetails: null,
                      });
                      setCartState(newCart);
                    } else {
                      addCartState(
                        {
                          name: "Cart Discount: $" + discountAmount,
                          price: -parseFloat(discountAmount),
                          description: "Discount Applied to Cart",
                          options: [],
                          extraDetails: null,
                        },
                        cart
                      );
                    }
                  }
                }
              }}
            >
              <Text style={{ textAlign: "center" }}>Apply</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </>
  );
};

export default DiscountModal;

const styles = StyleSheet.create({});
