import React, { Component, useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Image,
  Text,
  TouchableOpacity,
  Animated,
  Modal,
} from "react-native";
import { MaterialCommunityIcons, Entypo } from "@expo/vector-icons";
import ProductBuilderModal from "../ProductBuilderModal/ProductBuilderModal";

function CartItem({
  cartItem,
  index,
  removeAction,
  decreaseAction,
  increaseAction,
  style,
}) {
  const [showProductScreen, setshowProductScreen] = useState(false);
  const xPos = useRef(new Animated.Value(-1000)).current;
  const shadowOpacity = useRef(new Animated.Value(0)).current;
  const [isOpen, setisOpen] = useState(false);

  const fadeIn = () => {
    // Will change xPos value to 0 in 3 seconds
    setshowProductScreen(true);
    Animated.timing(xPos, {
      toValue: 0,
      duration: 100,
      useNativeDriver: false,
    }).start();
    Animated.timing(shadowOpacity, {
      toValue: 1,
      duration: 100,
      useNativeDriver: false,
    }).start();
  };

  const fadeOut = () => {
    // Will change xPos value to 0 in 3 seconds
    Animated.timing(shadowOpacity, {
      toValue: 0,
      duration: 100,
      useNativeDriver: false,
    }).start();
    Animated.timing(xPos, {
      toValue: -1000,
      duration: 100,
      useNativeDriver: false,
    }).start(() => setshowProductScreen(false));
  };

  return (
    <View style={[styles.container, style]}>
      <TouchableOpacity
        style={styles.topRowWithImgContainer}
        onPress={() => cartItem.options.length > 0 && setisOpen(true)}
        activeOpacity={0.8}
      >
        <Image
          source={require("../../assets/images/image_t93e..png")}
          resizeMode="contain"
          style={styles.cartItemImg}
        ></Image>
        <View style={styles.group}>
          <View style={styles.topRowTxt}>
            <Text style={styles.cartItemQuantity}>
              {cartItem.quantity ? cartItem.quantity : 1}
            </Text>
            <View style={styles.xPlusNameGroup}>
              <Text style={styles.txtX}>x</Text>
              <Text style={styles.veggiePizza}>{cartItem.name}</Text>
            </View>
            <Text style={styles.cartItemPrice}>
              ${parseFloat(cartItem.price).toFixed(2)}
            </Text>
          </View>
          {
            <View style={styles.bottomBtnRow}>
              <TouchableOpacity
                style={styles.cartItemIncreaseBtn}
                onPress={increaseAction}
              >
                <Entypo name="plus" style={styles.plusIcon}></Entypo>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.cartItemDecreaseBtn}
                onPress={() => {
                  if (cartItem.quantity < 2 || !cartItem.quantity) {
                    removeAction();
                  } else {
                    decreaseAction();
                  }
                }}
              >
                <Entypo name="minus" style={styles.minusIcon}></Entypo>
              </TouchableOpacity>
              <TouchableOpacity style={styles.cartItemEditBtn}>
                <MaterialCommunityIcons
                  name="pencil"
                  style={styles.pencilIcon}
                ></MaterialCommunityIcons>
              </TouchableOpacity>
            </View>
          }
          {!cartItem.quantityNotChangable ? (
            <View style={styles.closedRightContainer}>
              <TouchableOpacity
                onPress={() => {
                  if (cartItem.quantity < 2 || !cartItem.quantity) {
                    removeAction();
                  } else {
                    decreaseAction();
                  }
                }}
              >
                <Entypo name="squared-minus" style={styles.openCloseIcon} />
              </TouchableOpacity>
              <Text style={styles.productIndexTxt}>
                {cartItem.quantity ? cartItem.quantity : 1}
              </Text>
              <TouchableOpacity onPress={increaseAction}>
                <Entypo name="squared-plus" style={styles.openCloseIcon} />
              </TouchableOpacity>
            </View>
          ) : (
            <TouchableOpacity
              onPress={() => {
                removeAction();
              }}
            >
              <AntDesign
                name="closesquare"
                style={{ color: "#b1b1b1", fontSize: 30 }}
              />
            </TouchableOpacity>
          )}
        </View>
      </TouchableOpacity>
      {showProductScreen && (
        <Modal transparent={true}>
          <Animated.View
            style={{
              flex: 1,
              justifyContent: "flex-end",
              alignItems: "flex-start",
              position: "absolute",
              height: "100%",
              width: "100%",
              bottom: 0,
              left: xPos,
              zIndex: 0,
            }}
          >
            <View
              style={{
                height: "100%",
                width: "72%",
                borderTopRightRadius: 3,
              }}
            >
              <ProductBuilderModal
                product={cartItem.editableObj}
                itemIndex={index}
                goBack={() => fadeOut()}
              />
            </View>
          </Animated.View>
        </Modal>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "rgba(238,242,255,1)",
    borderRadius: 20,
    justifyContent: "center",
    alignItems: "center",
  },
  topRowWithImgContainer: {
    width: 280,
    height: 61,
    flexDirection: "row",
    alignItems: "flex-start",
    justifyContent: "space-between",
  },
  cartItemImg: {
    height: 61,
    width: 65,
  },
  group: {
    width: 207,
    height: 33,
    alignItems: "flex-end",
  },
  topRowTxt: {
    width: 206,
    height: 24,
    flexDirection: "row",
    justifyContent: "space-between",
    marginTop: 9,
  },
  cartItemQuantity: {
    fontWeight: "700",
    color: "#121212",
    fontSize: 20,
  },
  xPlusNameGroup: {
    width: 138,
    height: 24,
    flexDirection: "row",
    justifyContent: "flex-start",
    alignItems: "center",
  },
  txtX: {
    fontWeight: "700",
    color: "#00c93b",
    fontSize: 20,
    marginRight: 3,
  },
  veggiePizza: {
    fontWeight: "700",
    color: "#121212",
    fontSize: 19,
  },
  cartItemPrice: {
    fontWeight: "700",
    color: "#00c93b",
    fontSize: 18,
  },
  bottomBtnRow: {
    width: 77,
    height: 20,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginTop: 8,
  },
  cartItemIncreaseBtn: {
    width: 20,
    height: 20,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 100,
    alignItems: "center",
    justifyContent: "center",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 9,
    shadowOpacity: 0.1,
    shadowRadius: 3,
  },
  plusIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 18,
  },
  cartItemDecreaseBtn: {
    width: 20,
    height: 20,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 100,
    alignItems: "center",
    justifyContent: "center",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 9,
    shadowOpacity: 0.1,
    shadowRadius: 3,
  },
  minusIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 18,
  },
  cartItemEditBtn: {
    width: 20,
    height: 20,
    backgroundColor: "rgba(255,255,255,1)",
    borderRadius: 100,
    alignItems: "center",
    justifyContent: "center",
    shadowColor: "rgba(0,0,0,1)",
    shadowOffset: {
      width: 3,
      height: 3,
    },
    elevation: 9,
    shadowOpacity: 0.1,
    shadowRadius: 3,
  },
  pencilIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 12,
  },
});

export default CartItem;
