import React, { Component, useEffect, useState } from "react";
import {
  StyleSheet,
  View,
  Image,
  Text,
  TouchableOpacity,
  ScrollView,
  TextInput,
} from "react-native";
import ProductOptionBox from "./components/ProductOptionBox";
import { Feather, MaterialCommunityIcons } from "@expo/vector-icons";
import { onlineStoreState, setStoreDetailState, setUserStoreState, userState, userStoreState } from "state/state";
import { updateData } from "state/firebaseFunctions";
import { auth, db, storage } from "state/firebaseConfig";
import ProductImage from "components/ProductImage";
import { useHistory } from "react-router-dom";
import Swal from "sweetalert2";
import { AvocatImage } from "../../EntryFile/imagePath";
import CategoryOptionBox from "./components/CategoryOptionBox";
import AddCategoryModal from "./modals/AddCategoryModal";
import Modal from "react-native-modal";

function CategoryList(props) {
  const catalog = userStoreState.use()
  const [searchFilterValue, setsearchFilterValue] = useState('')
  const userS = userState.use();
  const onlineStoreDetails = onlineStoreState.use()
  const history = useHistory()
  const [editMode, seteditMode] = useState(false)
  const [addCategoryModal, setaddCategoryModal] = useState(false)
  const [editCategoryModal, seteditCategoryModal] = useState(f)

  const confirmText = (category) => {
    Swal.fire({
      title: "Are you sure?",
      text: "You won't be able to revert this!",
      type: "warning",
      showCancelButton: !0,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, delete it!",
      confirmButtonClass: "btn btn-primary",
      cancelButtonClass: "btn btn-danger ml-1",
      buttonsStyling: !1,
    }).then(function (t) {
      if (t.value) {
        Swal.fire({
          type: "success",
          title: "Deleted!",
          text: "Your file has been deleted.",
          confirmButtonClass: "btn btn-success",
        });
        const localCatalog = structuredClone(catalog);
        if (localCatalog.categories.length > 1) {
          // localCatalog.categories.splice(props.id - 2, 1);
          localCatalog.categories = catalog.categories.filter(
            (e) => e !== category
          );
        } else {
          localCatalog.categories = [];
        }

        db.collection("users")
          .doc(auth.currentUser?.uid)
          .update({
            categories: localCatalog.categories,
          })
          .catch((e) => console.log("ERROR HAS OCCURE FB: ", e));
        if (onlineStoreDetails.onlineStoreSetUp) {
          db.collection("public")
            .doc(auth.currentUser?.uid)
            .update({
              categories: localCatalog.categories,
            })
            .catch((e) => console.log("ERROR HAS OCCURE FB: ", e));
        }
      }
    });
  };

  useEffect(() => {
    catalog.categories.map((category, index) => {
      if (category.toLowerCase().includes(searchFilterValue.toLocaleLowerCase()) || !searchFilterValue) {
        const getItem = document.getElementById(index)
        if (getItem) {
          getItem.style.display = "flex";
        }
      }
      else {
        const getItem = document.getElementById(index)
        if (getItem) {
          getItem.style.display = "none";
        }
      }
    });
  }, [searchFilterValue,]);

  return (
    <View style={styles.container}>
      <View style={styles.topRow}>
        <Text style={styles.productManagementTxt}>Category Management</Text>
        <View>
          <TextInput style={styles.searchProductBox} placeholder="Search" value={searchFilterValue} onChangeText={(val) => setsearchFilterValue(val)} />
          <Feather name="search" style={{ color: 'grey', fontSize: 20, position: 'absolute', top: 5, right: 5 }} />
        </View>
        <TouchableOpacity style={styles.manageProductsBtn} onPress={() => seteditMode(prev => !prev)}>
          <MaterialCommunityIcons
            name="format-list-checks"
            style={styles.manageProductIcon}
          />
          <Text style={styles.manageProductsTxt}>Manage Categories</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.scrollArea}>
        <ScrollView
          contentContainerStyle={styles.scrollArea_contentContainerStyle}
        >
          <View style={styles.productsMap}>
            <TouchableOpacity style={[styles.addProductBtn, editMode && { height: 322 }]} onPress={() => setaddCategoryModal(true)}>
              <Feather
                name="plus"
                style={styles.addProductPlusIcon}
              />
              <Text style={styles.addNewItemTxt}>Add New Category</Text>
            </TouchableOpacity>
            {catalog.categories.map((category, index) => <div key={index} id={index}>
              <CategoryOptionBox
                style={[styles.productOptionBox, editMode && { height: 322 }]}
                index={index}
                category={category}
                editMode={editMode}
                deleteCategory={() => confirmText(category)}
                setEditCategoryModal={seteditCategoryModal}
              />
            </div>)}
          </View>
        </ScrollView>
      </View>
      <Modal
        isVisible={addCategoryModal}
        animationIn="fadeIn"
        animationOut="fadeOut"
      >
        <View
          style={{
            flex: 1,
            height: "100%",
            width: "100%",
            position: "absolute",
            left: 0,
            top: 0,
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <AddCategoryModal setaddCategoryModal={setaddCategoryModal} index={catalog.categories.length} />
        </View>
      </Modal>
      <Modal
        isVisible={editCategoryModal}
        animationIn="fadeIn"
        animationOut="fadeOut"
      >
        <View
          style={{
            flex: 1,
            height: "100%",
            width: "100%",
            position: "absolute",
            left: 0,
            top: 0,
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <AddCategoryModal setaddCategoryModal={seteditCategoryModal} index={catalog.categories.findIndex(e => e === editCategoryModal)} existingCategory={editCategoryModal} />
        </View>
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#ffffff",
    alignItems: "center",
    justifyContent: "flex-start", // Align items to the start to avoid stretching
    width: '100%',
  },
  topRow: {
    width: '95%',
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    marginTop: 25,
    marginBottom: 25,
  },
  scrollArea: {
    flex: 1, // Take up remaining space
    width: '95%',
  },
  scrollArea_contentContainerStyle: {
