    backgroundColor: "#fdfdff",
    borderWidth: 1,
    borderColor: "#000000",
    borderRadius: 10,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-around"
  },
  manageProductIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 22
  },
  manageProductsTxt: {
    fontWeight: '700',
    color: "#121212",
    fontSize: 14
  },
  categoriesScrollView: {
    width: '85%',
    marginBottom: 30,
  },
  categoriesScrollView_contentContainerStyle: {
    width: '100%',
  },
  categoryOpt1Txt: {
    // color: "#121212"
    color: 'grey',
    padding: 10
  },
  categoryOpt2Txt: {
    color: "#121212",
    marginLeft: 100
  },
  productsMap: {
    width: '100%',
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-between",
  },
  addProductBtn: {
    width: 215,
    height: 285,
    borderWidth: 3,
    borderColor: "#858585",
    borderRadius: 10,
    borderStyle: "dashed",
    backgroundColor: "#f5f5f5",
    alignItems: "center",
    justifyContent: "center",
    marginRight: 30,
    marginBottom: 30
  },
  addProductPlusIcon: {
    color: "rgba(0,0,0,1)",
    fontSize: 25
  },
  addNewItemTxt: {
    color: "#121212",
    fontSize: 16,
    marginTop: 20
  },
  productOptionBox: {
    height: 285,
    width: 215,
    marginLeft: 0,
    marginBottom: 30,
    marginRight: 30
  },
  productOptionBox1: {
    height: 285,
    width: 215,
    marginRight: 30,
    marginLeft: 0,
    marginBottom: 30
  },
  productOptionBox2: {
    height: 285,
