  return (
    <>
      <Image source={{ uri: source }} resizeMode={resizeMode} style={style} />
      <div
        id="loader"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100vh", // Viewport Height
          width: "100vw", // Viewport Width
          display: "flex",
          alignItems: "center", // This will center the content vertically
          justifyContent: "center", // This will center the content horizontally
        }}
      >
        <Animated.View
          style={{
            alignItems: "center",
            justifyContent: "center",
            backgroundColor: "white",
            position: "absolute",
            opacity: fadeAnim,
            height: "100%",
            width: "100%",
          }}
        >
          <Image
            source={require("assets/loading.gif")}
            style={{ width: 450, height: 450, resizeMode: "contain" }}
          />
        </Animated.View>
      </div>
    </>
  );
};

export default ProductImage
