import React, { Component } from "react";
import { StyleSheet, View, Text } from "react-native";

function OptionSelectionItem({ style, eInnerListStart, indexInnerList }) {
  return (
    <View style={[styles.container, props.style]}>
      <View style={styles.optionSelectionNameInputGroup}>
        <Text style={styles.selectionNameInputLbl}>Option Selection Name</Text>
        <View style={styles.selectionNameInput}></View>
      </View>
      <View style={styles.selectionPriceIncreaseInputGroup}>
        <Text style={styles.selectionPriceIncreaseLbl}>Price Increase</Text>
        <View style={styles.selectionPriceIncreaseInput}></View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center"
  },
  optionSelectionNameInputGroup: {
    width: 351,
    height: 84,
    justifyContent: "space-between"
  },
  selectionNameInputLbl: {
    color: "#121212",
    fontSize: 17
  },
  selectionNameInput: {
    width: 351,
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5
  },
  selectionPriceIncreaseInputGroup: {
    width: 352,
    height: 84,
    justifyContent: "space-between"
  },
  selectionPriceIncreaseLbl: {
    color: "#121212",
    fontSize: 17
  },
  selectionPriceIncreaseInput: {
    width: 351,
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5
  }
});

export default OptionSelectionItem;
