import { initializeApp } from 'firebase/app';
import {initializeAuth, getReactNativePersistence} from "firebase/auth/react-native";
import {getFirestore} from "firebase/firestore";
import AsyncStorage from '@react-native-async-storage/async-storage';

// Initialize Firebase
const firebaseConfig = {
  apiKey: "AIzaSyBPPC5M6DSqL34pKUYduvZKdVhzWzCWyYk",
  authDomain: "best-route-2412a.firebaseapp.com",
  projectId: "best-route-2412a",
  storageBucket: "best-route-2412a.appspot.com",
  messagingSenderId: "978892304125",
  appId: "1:978892304125:web:bcf953b0d4ea61f9f85894"
};

const app = initializeApp(firebaseConfig);

export const auth = initializeAuth(app, {
persistence: getReactNativePersistence(AsyncStorage)
});

export const db = getFirestore(app)
