import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React, { useState } from "react";
import { ListItem } from "@react-native-material/core";
import Icon from "@expo/vector-icons/MaterialCommunityIcons";

const ProductOptionDropDown = ({ label, options, setValue, value, style }) => {
  const [show, setShow] = useState(false);

  return (
    <View style={[styles.container, style]}>
      <TouchableOpacity
        onPress={() => setShow((prevState) => !prevState)}
        style={{
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "space-between",
          width: "100%",
        }}
      >
        <Text>
          {value
            ? `${value.label} : $${
                value.priceIncrease !== null ? value.priceIncrease : 0
              }`
            : label}
        </Text>
        <View style={{ flexDirection: "row", alignItems: "center" }}>
          <Text>|</Text>
          <Icon name={show ? "chevron-up" : "chevron-down"} />
        </View>
      </TouchableOpacity>
      {show && (
        <>
          {options.map((option, listIndex) => (
            <ListItem
              title={`${option.label} : $${
                option.priceIncrease !== null ? option.priceIncrease : 0
              }`}
              onPress={() => {
                setValue({
                  option: {
                    label: option.label,
                    priceIncrease:
                      option.priceIncrease !== null ? option.priceIncrease : 0,
                  },
                  listIndex: listIndex,
                });
                setShow(false);
              }}
            />
          ))}
        </>
      )}
    </View>
  );
};

export default ProductOptionDropDown;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
  },
});
