import { Button } from "@react-native-material/core";
import React, { useEffect, useState } from "react";
import { TouchableOpacity, View, Text } from "react-native";
import { auth } from "state/firebaseConfig";
import { userStoreState } from "state/state";
// import { useNavigation } from "react-navigation-hooks";

const CustomHeader = ({
  navigation,
  route,
  options,
  back,
  section,
  setSection,
}) => {
  const [settings, setSettings] = useState(false);
  const catalog = userStoreState.use();

  useEffect(() => {
    const prevScreen = localStorage.getItem("localScreen");
    setSection(prevScreen);
    if (
      prevScreen === "add-category" ||
      prevScreen === "add-product" ||
      prevScreen === "edit-woocommerce" ||
      prevScreen === "edit-store-details"
    )
      setSettings(true);
  }, []);

    useEffect(() => {
        if (!settings) {

      }
    }, [third])


  if (settings === true) {
    //edit settings menu
    return (
      <View
        style={{
          width: "100%",
          height: 120,
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "space-evenly",
          backgroundColor: "red",
        }}
      >
        <Button
          titleStyle={
            section === "add-category"
              ? { color: "white", fontWeight: "700" }
              : { color: "black", fontWeight: "700" }
          }
          style={
            section === "add-category"
              ? { backgroundColor: "blue" }
              : { backgroundColor: "white" }
          }
          onPress={() => {
            setSection("add-category");
            localStorage.setItem("localScreen", "add-category");
            navigation.navigate("EditCategories");
          }}
          title="Add Category"
        />
        <Button
          titleStyle={
            section === "add-product"
              ? { color: "white", fontWeight: "700" }
              : { color: "black", fontWeight: "700" }
          }
          style={
            section === "add-product"
              ? { backgroundColor: "blue" }
              : { backgroundColor: "white" }
          }
          onPress={() => {
            setSection("add-product");
            localStorage.setItem("localScreen", "add-product");
            navigation.navigate("EditProductList");
          }}
          title="Add Product"
        />
        <Button
          titleStyle={
            section === "edit-woocommerce"
              ? { color: "white", fontWeight: "700" }
              : { color: "black", fontWeight: "700" }
          }
          style={
            section === "edit-woocommerce"
              ? { backgroundColor: "blue" }
              : { backgroundColor: "white" }
          }
          onPress={() => {
            setSection("edit-woocommerce");
            localStorage.setItem("localScreen", "edit-woocommerce");
            navigation.navigate("EditWoocommerce");
          }}
          title="Woocommerce"
        />
        <Button
          titleStyle={
            section === "edit-store-details"
              ? { color: "white", fontWeight: "700" }
              : { color: "black", fontWeight: "700" }
          }
          style={
            section === "edit-store-details"
              ? { backgroundColor: "blue" }
              : { backgroundColor: "white" }
          }
          onPress={() => {
            setSection("edit-store-details");
            localStorage.setItem("localScreen", "edit-store-details");
            navigation.navigate("StoreDetails");
          }}
          title="Store Details"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => {
            localStorage.removeItem("localScreen");
            auth.signOut();
          }}
          title="Logout"
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => {
            setSettings(false);
            setSection();
            localStorage.removeItem("localScreen");
          }}
          title="X"
        />
      </View>
    );
  }
  return (
    <View
      style={{
        width: "100%",
        height: 120,
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "space-evenly",
        backgroundColor: "red",
      }}
    >
      {catalog.categories?.map((category, index) => {
        if (!section && index === 0) {
          return (
            <Button
              key={index}
              titleStyle={{ color: "white", fontWeight: "700" }}
              style={{ backgroundColor: "blue" }}
              onPress={() => {
                setSection(category);
              }}
              title={category}
            />
          );
        }

        return (
          <Button
            key={index}
            titleStyle={
              section === category
                ? { color: "white", fontWeight: "700" }
                : { color: "black", fontWeight: "700" }
            }
            style={
              section === category
                ? { backgroundColor: "blue" }
                : { backgroundColor: "white" }
            }
            onPress={() => {
              setSection(category);
              localStorage.setItem("localScreen", category);
            }}
            title={category}
          />
        );
      })}
      <Button
        titleStyle={
          section === "transList"
            ? { color: "white", fontWeight: "700" }
            : { color: "black", fontWeight: "700" }
        }
        style={
          section === "transList"
            ? { backgroundColor: "blue" }
            : { backgroundColor: "white" }
        }
        onPress={() => {
          navigation.navigate("Transactions");
        }}
        title="Transaction"
      />
      <Button
        titleStyle={{ color: "black", fontWeight: "700" }}
        style={{ backgroundColor: "white" }}
        onPress={() => setSettings(true)}
        title="Settings"
      />
    </View>
  );
};

export default CustomHeader;
