import {
  FlatList,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  useWindowDimensions,
  View,
} from "react-native";
import React, { useEffect, useMemo, useRef, useState } from "react";
import { Button, TextInput } from "@react-native-material/core";
import DropDown from "./DropDown";
import { userStoreState } from "state/state";
import { updateData } from "state/firebaseFunctions";
import OptionView from "./OptionView";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import Feather from "@expo/vector-icons/Feather";
import ReactSelect from "react-select";

const Confirmation = ({ confirmLbl, yesAction, noAction }) => {
  const { width, height } = useWindowDimensions();

  return (
    <View
      style={{
        height: "100%",
        width: "100%",
      }}
    >
      <TouchableOpacity
        style={{
          backgroundColor: "black",
          opacity: 0.5,
          height: "100%",
          width: "100%",
        }}
        onPress={noAction}
      />
      <View
        style={{
          height: height * 0.9,
          width: height * 0.9,
          backgroundColor: "white",
          borderRadius: 10,
          position: "absolute",
          marginLeft: "auto",
          marginRight: "auto",
          marginTop: "auto",
          marginBottom: "auto",
          left: 0,
          right: 0,
          top: 0,
          bottom: 0,
        }}
      >
        <View
          style={{
            width: "100%",
            alignItems: "center",
            justifyContent: "space-between",
            flexDirection: "row",
            padding: 25,
          }}
        >
          <TouchableOpacity
            onPress={noAction}
            style={{
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons
              name="chevron-left"
              size={32}
              color="#4A4A4A"
            />
          </TouchableOpacity>
          <Text style={{ fontFamily: "archivo-600", fontSize: 24 }}>
            {confirmLbl}
          </Text>
        </View>
        <Button
          title="Yes"
          onPress={yesAction}
          style={{
            marginBottom: 25,
            marginTop: 25,
            backgroundColor: "#4050B5",
          }}
        />
        <Button
          title="No"
          onPress={noAction}
          style={{
            marginBottom: 25,
            marginTop: 25,
            backgroundColor: "#4050B5",
          }}
        />
      </View>
    </View>
  );
};

export default Confirmation;
