import { Button } from "@react-native-material/core";
import React, { useState } from "react";
import { TouchableOpacity, View, Text } from "react-native";
import { auth } from "state/firebaseConfig";
// import { useNavigation } from "react-navigation-hooks";

const CustomHeader = ({ navigation, route, options, back }) => {
    const [settings, setSettings] = useState(false);

    const Menu = () => {
      if (settings === true) {
        //edit settings menu
        return (
          <View
            style={{
              width: "100%",
              height: 120,
              flexDirection: "row",
              alignItems: "center",
              justifyContent: "space-evenly",
              backgroundColor: "red",
            }}
          >
            <Button
              titleStyle={
                section === "add-category"
                  ? { color: "white", fontWeight: "700" }
                  : { color: "black", fontWeight: "700" }
              }
              style={
                section === "add-category"
                  ? { backgroundColor: "blue" }
                  : { backgroundColor: "white" }
              }
              onPress={() => {
                navigation.navigate("EditCategories");
              }}
              title="Add Category"
            />
            <Button
              titleStyle={
                section === "add-product"
                  ? { color: "white", fontWeight: "700" }
                  : { color: "black", fontWeight: "700" }
              }
              style={
                section === "add-product"
                  ? { backgroundColor: "blue" }
                  : { backgroundColor: "white" }
              }
              onPress={() => {
                navigation.navigate("EditProductList");
              }}
              title="Add Product"
            />
            <Button
              titleStyle={
                section === "edit-woocommerce"
                  ? { color: "white", fontWeight: "700" }
                  : { color: "black", fontWeight: "700" }
              }
              style={
                section === "edit-woocommerce"
                  ? { backgroundColor: "blue" }
                  : { backgroundColor: "white" }
              }
              onPress={() => {
                navigation.navigate("EditWoocommerce");
              }}
              title="Woocommerce"
            />
            <Button
              titleStyle={
                section === "edit-store-details"
                  ? { color: "white", fontWeight: "700" }
                  : { color: "black", fontWeight: "700" }
              }
              style={
                section === "edit-store-details"
                  ? { backgroundColor: "blue" }
                  : { backgroundColor: "white" }
              }
              onPress={() => {
                navigation.navigate("StoreDetails");
              }}
              title="Store Details"
            />
            <Button
              titleStyle={{ color: "black", fontWeight: "700" }}
              style={{ backgroundColor: "white" }}
              onPress={() => {
                auth.signOut();
              }}
              title="Logout"
            />
            <Button
              titleStyle={{ color: "black", fontWeight: "700" }}
              style={{ backgroundColor: "white" }}
              onPress={() => {
                setSettings(false);
                setSection();
              }}
              title="X"
            />
          </View>
        );
      }
      return (
        <View
          style={{
            width: "100%",
            height: 120,
            flexDirection: "row",
            alignItems: "center",
            justifyContent: "space-evenly",
            backgroundColor: "red",
          }}
        >
          {catalog.categories?.map((category, index) => {
            if (!section && index === 0) {
              return (
                <Button
                  key={index}
                  titleStyle={{ color: "white", fontWeight: "700" }}
                  style={{ backgroundColor: "blue" }}
                  onPress={() => {
                    setSection(category);
                  }}
                  title={category}
                />
              );
            }

            return (
              <Button
                key={index}
                titleStyle={
                  section === category
                    ? { color: "white", fontWeight: "700" }
                    : { color: "black", fontWeight: "700" }
                }
                style={
                  section === category
                    ? { backgroundColor: "blue" }
                    : { backgroundColor: "white" }
                }
                onPress={() => {
                  setSection(category);
                  localStorage.setItem("localScreen", category);
                }}
                title={category}
              />
            );
          })}
          <Button
            titleStyle={
              section === "transList"
                ? { color: "white", fontWeight: "700" }
                : { color: "black", fontWeight: "700" }
            }
            style={
              section === "transList"
                ? { backgroundColor: "blue" }
                : { backgroundColor: "white" }
            }
            onPress={() => {
              navigation.navigate("Transactions");
            }}
            title="Transaction"
          />
          <Button
            titleStyle={{ color: "black", fontWeight: "700" }}
            style={{ backgroundColor: "white" }}
            onPress={() => setSettings(true)}
            title="Settings"
          />
        </View>
      );
    };
};

export default CustomHeader;
