import { StyleSheet, Text, View } from "react-native";
import React, { useState } from "react";
import {
  deserts,
  drinks,
  specialty,
  salads,
  sandwhiches,
  sauces,
  sides,
} from "state/allItems";
import ListItem from "components/ListItem";
import ComboScreen from "components/ComboScreen";
import DrinkCombo from "components/DrinkCombo";
import SpecialtyItem from "components/SpecialtyItem";
import Panzarotti from "components/Panzarotti";
import BuildPizza from "components/BuildPizza";
import GlutenFreePizza from "components/GlutenFreePizza";

const Section = ({ section }) => {
  if (section === "combo") return <ComboScreen />;
  if (section === "specialty")
    return (
      <View style={styles.wrapper}>
        <BuildPizza />
        <GlutenFreePizza />
        {specialty.map((item) => (
          <SpecialtyItem pizza={item} key={item.name} />
        ))}
      </View>
    );
  if (section === "sides")
    return (
      <View style={styles.wrapper}>
        {sides.map((item) => (
          <ListItem item={item} key={item.name} />
        ))}
        <Panzarotti />
      </View>
    );
  if (section === "salads")
    return (
      <View style={styles.wrapper}>
        {salads.map((item) => (
          <ListItem item={item} key={item.name} />
        ))}
      </View>
    );
  if (section === "sandwhiches")
    return (
      <View style={styles.wrapper}>
        {sandwhiches.map((item) => (
          <ListItem item={item} key={item.name} />
        ))}
      </View>
    );
  if (section === "sauces")
    return (
      <View style={styles.wrapper}>
        {sauces.map((item) => (
          <ListItem item={item} key={item.name} />
        ))}
      </View>
    );
  if (section === "drinks")
    return (
      <View style={styles.wrapper}>
        {drinks.map((item) => (
          <ListItem item={item} key={item.name} />
        ))}
        <DrinkCombo />
      </View>
    );
  if (section === "deserts")
    return (
      <View style={styles.wrapper}>
        {deserts.map((item) => (
          <ListItem item={item} key={item.name} />
        ))}
      </View>
    );

  return <View>{/* <Text>{allItems[0]}</Text> */}</View>;
};

const MenuScreen = ({ section, categories, products }) => {
products.filter((current))

  return (
    <View style={styles.container}>
      <Section section={section} />
    </View>
  );
};

export default MenuScreen;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
    flex: 2,
    height: "100%",
  },
  wrapper: {
    flex: 1,
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-between",
    paddingLeft: 50,
    paddingRight: 50,
    height: "100%",
    width: "100%",
    alignItems: "center",
  },
});
