import { View, Text } from 'react-native'
import React from 'react'
import ProductDisplayBtn from "components/ProductDisplayBtn";

const MenuScreenInnerBlock = ({catalog, section, height}) => {
  if (catalog.products) {
    if (catalog.products.length > 0) {
        const correctProducts = catalog.products.filter(
          (e) => e.catagory === section || e.category === section
        );

        if (correctProducts.length > 0) {
          return correctProducts.map((product, index) => (
            <ProductDisplayBtn
              product={product}
              productIndex={index}
              key={index}
            />
          ));
        } else {
          return (
            <View
              style={{
                width: "100%",
                height: height * 0.8,
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <Text
                style={{
                  fontFamily: "archivo-500",
                  color: "rgba(74,74,74,1)",
                  fontSize: 20,
                }}
              >
                This category has no products...
              </Text>
            </View>
          );
        }
    }
  }
}

export default MenuScreenInnerBlock
