import React, { Component, useEffect, useRef, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TouchableOpacity,
  ScrollView,
  Modal,
} from "react-native";
import { Entypo } from "@expo/vector-icons";

function GeneralDropdown({
  style,
}) {
  const dropdownRef = useRef(); // Reference to the original button
  const [dropdownLayout, setDropdownLayout] = useState();
  const [openDropdown, setopenDropdown] = useState(false);

  useEffect(() => {
    // Ensure this code runs in a web environment
    if (dropdownRef.current && typeof window !== "undefined") {
      const element = dropdownRef.current; // Assuming this ref points to a DOM element
      // You might need to adjust this to get the actual DOM node in React Native Web

      const boundingRect = element.getBoundingClientRect();

      setDropdownLayout({
        x: boundingRect.left,
        y: boundingRect.top, // Adjust based on scroll position
        width: boundingRect.width,
        height: boundingRect.height,
      });
    }
  }, []); // Recalculate when scroll position changes

  return (
    <View style={{ zIndex: 1000 }}>
      <TouchableOpacity
        style={styles.userBtn}
        activeOpacity={0.8}
        onPress={() => setopenDropdown(prev => !prev)}
        ref={dropdownRef}
      >
        <TouchableOpacity
          style={styles.userBtn}
          activeOpacity={0.8}
          onPress={() => setopenDropdown(true)}
        >
          <View style={styles.iconWithNameGroup}>
            <Image
              source={require("./assets/images/image_bTyU..png")}
              resizeMode="contain"
              style={styles.userIcon}
            />
            <Text style={styles.username}>{storeDetails.name}</Text>
          </View>
          <Entypo name="chevron-small-down" style={styles.chevronDownIcon} />
          <Modal visible={logoutDropdown} transparent={true}>
            <TouchableOpacity
              style={{
                backgroundColor: "rgba(255,255,255,1)",
                borderRadius: 10,
                shadowColor: "rgba(0,0,0,1)",
                shadowOffset: {
                  width: 3,
                  height: 3,
                },
                elevation: 30,
                shadowOpacity: 0.2,
                shadowRadius: 10,
                flexDirection: "row",
                alignItems: "center",
                justifyContent: "space-around",
                width: "100%",
                height: 43,
                position: "absolute",
                bottom: -46,
                left: 0,
              }}
            >
              <Text style={styles.logoutFromAccount}>Logout From Account</Text>
              <Feather name="log-out" style={styles.logoutIcon} />
            </TouchableOpacity>
          </Modal>
        </TouchableOpacity>
      </TouchableOpacity>
      <Modal visible={openDropdown} transparent={true}>
        <TouchableOpacity
          style={{
            width: "100%",
            height: "100%",
          }}
          onPress={() => {
            setopenDropdown(false);
          }} // Hide modal when the background is pressed
        />
        {dropdownLayout && (
          <View
            style={{
              position: "absolute",
              top: dropdownLayout.y,
              left: dropdownLayout.x,
              width: 190,
            }}
          >
            <TouchableOpacity
              style={[
                styles.dropdown,
                {
                  borderBottomLeftRadius: 0,
                  borderBottomRightRadius: 0,
                  borderBottomColor: "black",
                },
              ]}
              onPress={() => setopenDropdown((prev) => !prev)}
            >
              <Text style={styles.placeholder}>
                {value ? value : placeholder}
              </Text>
              <Entypo
                name={openDropdown ? "chevron-small-up" : "chevron-small-down"}
                style={styles.downIcon}
              />
            </TouchableOpacity>
            {openDropdown && (
              <ScrollView
                style={{
                  width: 190,
                  position: "absolute",
                  backgroundColor: "white",
                  bottom: options.length > 3 ? -50 * 3 : -50 * options.length,
                  height: options.length > 3 ? 50 * 3 : 50 * options.length,
                  borderBottomLeftRadius: 5,
                  borderBottomRightRadius: 5,
                  borderWidth: 1,
                  borderTopWidth: 0,
                  borderColor: "#9e9e9e",
                }}
              >
                {options.map((option, listIndex) => (
                  <TouchableOpacity
                    key={listIndex}
                    onPress={() => {
                      setValue(option);
                      setopenDropdown(false);
                    }}
                    style={[
                      {
                        width: "100%",
                        height: 50,
                        backgroundColor: "white",
                        padding: 10,
                        justifyContent: "center",
                      },
                      listIndex < options.length - 1 && {
                        borderBottomWidth: 1,
                        borderColor: "#9e9e9e",
                      },
                    ]}
                    activeOpacity={1}
                  >
                    <Text style={styles.placeholder}>{option}</Text>
                  </TouchableOpacity>
                ))}
              </ScrollView>
            )}
          </View>
        )}
      </Modal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 5,
    borderWidth: 1,
    borderColor: "#9e9e9e",
  },
  dropdown: {
    width: 190,
    height: 50,
    backgroundColor: "rgba(255,255,255,1)",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    borderRadius: 5,
    borderWidth: 1,
    borderColor: "#9e9e9e",
    paddingLeft: 10,
    paddingRight: 10,
  },
  placeholder: {
    color: "grey",
    fontSize: 14,
  },
  downIcon: {
    color: "rgba(128,128,128,1)",
    fontSize: 30,
    margin: 0,
    marginTop: 2,
    marginRight: 2,
  },
});

export default GeneralDropdown;
