import React, { useEffect, useState } from "react";
import { employeesState, myDeviceDetailsState, storeDetailState, woocommerceState } from "state/state";
import { auth, db } from "state/firebaseConfig";
const tz = require("moment-timezone");
import ReceiptPrint from "components/ReceiptPrint";
import { Button, Modal, Text, View } from "react-native";

const EmployeeReport = () => {
    const employees = employeesState
    const [addEmployeeModal, setAddEmployeeModal] = useState(false)

    return (
        <div className="page-wrapper">
            <div className="content">
                <div className="page-header">
                    <div className="page-title">
                        <h4>Employee Report</h4>
                        <h6>Manage your Employee Report</h6>
                    </div>
                </div>
                {/* /product list */}
                <div className="card">
                    <div className="card-body">
                        <Button title="Add Employee" onPress={() => setAddEmployeeModal(true)} />
                        {employees.length > 0 ? employees.map((employee, index) => {
                            return (
                                <View key={index}>
                                    <Text>{employee.name}</Text>
                                </View>)
                        }) : <Text>No Employees</Text>}
                        <Modal visible={addEmployeeModal}>
                            <View>
                                <Text>Add Employee</Text>
                            </View>
                        </Modal>
                    </div>
                </div>
                {/* /product list */}
            </div>
        </div>
    );
};

export default EmployeeReport;
