import React, { Component } from "react";
import { StyleSheet, TouchableOpacity, Image, Text } from "react-native";
import Icon from "react-native-vector-icons/Feather";

function ProductOptionBox({style, product, index}) {
  return (
    <TouchableOpacity style={[styles.container, style]}>
      <Image
        source={require("../assets/images/image_DUKP..png")}
        resizeMode="contain"
        style={styles.productImage}
      ></Image>
      <Text style={styles.productNameTxt}>Product Type</Text>
      <Text style={styles.productPriceTxt}>$12</Text>
      <TouchableOpacity style={styles.editProductBtn}>
        <Icon name="edit-3" style={styles.editProductIcon}></Icon>
        <Text style={styles.editProductTxt}>Edit Product</Text>
      </TouchableOpacity>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    borderRadius: 10,
    backgroundColor: "#f5f5f5",
    alignItems: "center",
    justifyContent: "space-between",
  },
  productImage: {
    height: 125,
    width: 127,
    marginTop: 20,
  },
  productNameTxt: {
    color: "#121212",
    fontSize: 15,
  },
  productPriceTxt: {
    fontWeight: "700",
    color: "#20c85c",
    fontSize: 20,
  },
  editProductBtn: {
    width: 215,
    height: 37,
    borderRadius: 10,
    borderBottomRightRadius: 10,
    borderBottomLeftRadius: 10,
    borderTopLeftRadius: 0,
    borderTopRightRadius: 0,
    backgroundColor: "#2b3659",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  editProductIcon: {
    color: "rgba(255,254,254,1)",
    fontSize: 20,
  },
  editProductTxt: {
    color: "rgba(255,255,255,1)",
    fontSize: 14,
    marginLeft: 10,
  },
});

export default ProductOptionBox;
