import React, { Component } from "react";
import { StyleSheet, View, Text } from "react-native";

function OnlineStoreSettings() {
    const onlineStoreDetails = onlineStoreState.use()
    const storeDetails = storeDetailState.use()
    const catalog = userStoreState.use()
    const [urlEnding, seturlEnding] = useState(onlineStoreDetails.urlEnding)
    const [stripePublicKey, setstripePublicKey] = useState(onlineStoreDetails.stripePublicKey)
    const [stripeSecretKey, setstripeSecretKey] = useState(onlineStoreDetails.stripeSecretKey)
    const fadeAnim = useRef(new Animated.Value(0)).current;
    const [viewVisible, setviewVisible] = useState(false);

    const fadeIn = () => {
        // Will change fadeAnim value to 0 in 3 seconds
        Animated.timing(fadeAnim, {
            toValue: 1,
            duration: 500,
            useNativeDriver: false,
        }).start();
    };

    const resetLoader = () => {
        setviewVisible(true);
        fadeIn();
    };

    const startOnlineStore = () => {
        db.collection("public").where("urlEnding", "==", urlEnding)
            .get()
            .then((querySnapshot) => {
                if (querySnapshot.empty) {
                    db.collection("public").doc(auth.currentUser.uid).set({
                        storeDetails: storeDetails,
                        categories: catalog.categories,
                        urlEnding: urlEnding,
                    }).then(() => {
                        catalog.products.forEach((product) => {
                            db.collection("public").doc(auth.currentUser.uid).collection("products").doc(product.id).set(product)
                        })
                        db.collection('users').doc(auth.currentUser.uid).update({
                            onlineStoreActive: true,
                            onlineStoreSetUp: true,
                            urlEnding: urlEnding
                        })
                        setOnlineStoreState({
                            onlineStoreActive: true,
                            onlineStoreSetUp: true,
                            urlEnding: urlEnding
                        })
                    })
                } else {
                    alert("This url ending is already taken. Please choose another one.")
                }
            })
            .catch((error) => {
                console.log("Error getting documents: ", error);
            });
    }

    const updateStripeDetails = () => {
        db.collection('users').doc(auth.currentUser.uid).update({
            stripePublicKey: stripePublicKey,
            stripeSecretKey: stripeSecretKey
        })
        db.collection("public").doc(auth.currentUser.uid).update({
            stripePublicKey: stripePublicKey
        })
        setOnlineStoreState({
            ...onlineStoreDetails,
            stripePublicKey: stripePublicKey,
            stripeSecretKey: stripeSecretKey
        })
    }

    const makeOnlineStoreActive = () => {
        if (!onlineStoreDetails.onlineStoreActive) {
            catalog.products.forEach((product) => {
                db.collection("public").doc(auth.currentUser.uid).collection("products").doc(product.id).set(product)
            })
            db.collection('users').doc(auth.currentUser.uid).update({
                onlineStoreActive: true,
                onlineStoreSetUp: true,
                urlEnding: urlEnding,
            })
            db.collection('public').doc(auth.currentUser.uid).update({
                onlineStoreActive: true,
                onlineStoreSetUp: true,
                urlEnding: urlEnding,
                storeDetails: storeDetails,
                categories: catalog.categories,
            })
            setOnlineStoreState({
                ...onlineStoreDetails,
                onlineStoreActive: true,
            })
        } else {
            db.collection('users').doc(auth.currentUser.uid).update({
                onlineStoreActive: false,
                onlineStoreSetUp: true,
                urlEnding: urlEnding
            })
            db.collection('public').doc(auth.currentUser.uid).update({
                onlineStoreActive: false,
                onlineStoreSetUp: true,
                urlEnding: urlEnding
            })
            setOnlineStoreState({
                ...onlineStoreDetails,
                onlineStoreActive: false,
            })
        }
    }

    const payOnlineStore = async () => {
        resetLoader()

        await db
            .collection("users")
            .doc(auth.currentUser.uid)
            .collection("checkout_sessions")
            .add({
                // price: 'price_1Ocw7JCIw3L7DOwIWpAyVUiB', // real price
                price: 'price_1Ocw7JCIw3L7DOwIWpAyVUiB', // real price
                success_url: window.location.href, // return user to this screen on successful purchase
                cancel_url: window.location.href, // return user to this screen on failed purchase
            })
            .then((docRef) => {
                // Wait for the checkoutSession to get attached by the extension
                docRef.onSnapshot(async (snap) => {
                    const { error, sessionId } = snap.data();
                    if (error) {
                        // Show an error to your customer and inspect
                        // your Cloud Function logs in the Firebase console.
                        alert(`An error occurred: ${error.message}`);
                    }

                    if (sessionId) {
                        // We have a session, let's redirect to Checkout
                        // Init Stripe
                        const stripe = await loadStripe(
                            "pk_live_51MHqrvCIw3L7DOwI0ol9CTCSH7mQXTLKpxTWKzmwOY1MdKwaYwhdJq6WTpkWdBeql3sS44JmybynlRnaO2nSa1FK001dHiEOZO" // todo enter your public stripe key here
                        );
                        console.log(`redirecting`);
                        await stripe.redirectToCheckout({ sessionId });
                    }
                });
            });
    }

    return (
        <View style={styles.container}>
            <View style={styles.headerContainer}>
                <Text style={styles.onlineStoreSettingsHeader}>
                    Online Store Settings
                </Text>
            </View>
            <View style={styles.onlineStoreInnerContainer}>
                <View style={styles.innerGroup}>
                    <View style={styles.inputsGroup}>
                        <View style={styles.urlEndingInputGroup}>
                            <Text style={styles.onlineUrlEndingTxt}>Online URL Ending</Text>
                            <View style={styles.uRLBox}></View>
                        </View>
                        <View style={styles.stripePublicKeyInputGroup}>
                            <Text style={styles.stripePublicKeyTxt}>Stripe Public Key</Text>
                            <View style={styles.stripeKeyBox}></View>
                        </View>
                        <View style={styles.stripePrivateKeyInputGroup}>
                            <Text style={styles.stripePrivateKeyTxt}>Stripe Private Key</Text>
                            <View style={styles.stripePrivateKeyBox}></View>
                        </View>
                    </View>
                    <View style={styles.bottomBtnGroup}>
                        <Text style={styles.readInfo}>
                            *Once Confirmed This Information CAN NOT BE CHANGED
                        </Text>
                        <View style={styles.confirmBtn}>
                            <Text style={styles.confirmTxtBtn}>Confirm</Text>
                        </View>
                    </View>
                </View>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        alignItems: "flex-end",
        justifyContent: "space-between",
        width: 691,
        height: 562
    },
    headerContainer: {
        height: 19,
        alignSelf: "stretch"
    },
    onlineStoreSettingsHeader: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 16
    },
    onlineStoreInnerContainer: {
        width: 499,
        height: 485,
        backgroundColor: "#ffffff",
        borderWidth: 1,
        borderColor: "#bfc1cb",
        shadowColor: "#b6b8c2",
        shadowOffset: {
            width: 3,
            height: 3
        },
        elevation: 600,
        shadowOpacity: 1,
        shadowRadius: 10,
        justifyContent: "center",
        alignItems: "center"
    },
    innerGroup: {
        width: 358,
        height: 419,
        justifyContent: "space-between",
        alignItems: "center"
    },
    inputsGroup: {
        width: 358,
        height: 291,
        justifyContent: "space-between"
    },
    urlEndingInputGroup: {
        width: 358,
        height: 86,
        justifyContent: "space-between"
    },
    onlineUrlEndingTxt: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 17
    },
    uRLBox: {
        width: 358,
        height: 50,
        backgroundColor: "#ffffff",
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#9b9b9b"
    },
    stripePublicKeyInputGroup: {
        width: 358,
        height: 87,
        justifyContent: "space-between"
    },
    stripePublicKeyTxt: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 17
    },
    stripeKeyBox: {
        width: 358,
        height: 50,
        backgroundColor: "#ffffff",
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#9b9b9b"
    },
    stripePrivateKeyInputGroup: {
        width: 358,
        height: 86,
        justifyContent: "space-between"
    },
    stripePrivateKeyTxt: {
        fontWeight: '700',
        color: "#121212",
        fontSize: 17
    },
    stripePrivateKeyBox: {
        width: 358,
        height: 50,
        backgroundColor: "#ffffff",
        borderRadius: 5,
        borderWidth: 1,
        borderColor: "#9b9b9b"
    },
    bottomBtnGroup: {
        width: '100%',
        height: 69,
        justifyContent: 'center',
        alignItems: 'center'
    },
    readInfo: {
        color: "rgba(0,0,0,1)",
        fontSize: 12
    },
    confirmBtn: {
        width: 173,
        height: 46,
        backgroundColor: "#1c294e",
        borderRadius: 20,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 22,
    },
    confirmTxtBtn: {
        fontWeight: '700',
        color: "rgba(255,245,245,1)",
        fontSize: 14
    }
});

export default OnlineStoreSettings;
