import { View, Text, Modal } from "react-native";
import React, { useEffect, useRef, useState } from "react";

//typscript type for props
type Props = {
    children: React.ReactNode;
    step: number;
    helpDescription: string;
};

const TutorialStep = (props: Props) => {
  const { children, step, helpDescription } = props;

  // X
  const [x, setX] = useState();

  // Y
    const [y, setY] = useState();

    //DescriptionViewWidth
    const [descriptionViewWidth, setDescriptionViewWidth] = useState();

  return (
    <>
      <Modal transparent>
        <View
          style={{
            backgroundColor: "rgba(0,0,0,0.5)",
            position: "absolute",
            height: "100%",
            width: "100%",
          }}
        />
        <View style={{ position: "absolute", top: y, left: x }}>
          {children}
        </View>
        <View
          onLayout={(event) => {
            const layout = event.nativeEvent.layout;
            console.log(layout);
            setDescriptionViewWidth(layout.width);
          }}
          style={{
            position: "absolute",
            top: y + 100,
            left: x - descriptionViewWidth / 2,
            padding: 20,
            backgroundColor: "white",
          }}
        >
          <View
            style={{
              width: 0,
              height: 0,
              backgroundColor: "transparent",
              borderStyle: "solid",
              borderLeftWidth: 12.5,
              borderRightWidth: 12.5,
              borderBottomWidth: 25,
              borderLeftColor: "transparent",
              borderRightColor: "transparent",
              borderBottomColor: "red",
              position: "absolute",
              top: 0,
              left: x - descriptionViewWidth / 2,
            }}
          />
          <Text style={{ marginBottom: 20 }}>{helpDescription}</Text>
          <View
            style={{
              flexDirection: "row",
              alignItems: "center",
              justifyContent: "flex-end",
            }}
          >
            <Text style={{ marginRight: 15 }}>Previous</Text>
            <Text>Next</Text>
          </View>
        </View>
      </Modal>
      <View
        onLayout={(event) => {
          const layout = event.nativeEvent.layout;
          console.log(layout);
          setX(layout.left);
          setY(layout.top);
        }}
      >
        {children}
      </View>
    </>
  );
};

export default TutorialStep;
