  const [showProductScreen, setshowProductScreen] = useState(false);

  return (
    <>
      <CartItem
        cartItem={cartItem}
        index={index}
        isPrev={false}
        removeAction={removeAction}
        editAction={() => setshowProductScreen(true)}
      />
      {showProductScreen && (
        <Modal transparent={true}>
          <View
            style={{
              flex: 1,
              justifyContent: "flex-end",
              alignItems: "flex-start",
            }}
          >
            <View style={{ height: "90%", width: "70%" }}>
