import { View, Text } from "react-native";
import React from "react";
import ProductDisplayBtn from "components/ProductDisplayBtn";
import { userStoreState } from "state/state";

const MenuScreenInnerBlock = ({ section, height }) => {
  const catalog = userStoreState.use();

  if (catalog.products?.length > 0) {
    const correctProducts = section
      ? catalog.products.filter(
          (e) => e.catagory === section || e.category === section
        )
      : catalog.products.filter(
          (e) =>
            e.catagory === catalog.categories[0] ||
            e.category === catalog.categories[0]
        );

    if (correctProducts.length > 0) {
        return catalog.products.map((product, index) => {
            return (

                <ProductDisplayBtn product={product} productIndex={index} key={index} />
            )
        });
    } else {
      return (
        <View
          style={{
            width: "100%",
            height: height * 0.8,
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Text
            style={{
              fontFamily: "archivo-500",
              color: "rgba(74,74,74,1)",
              fontSize: 20,
            }}
          >
            This category has no products...
          </Text>
        </View>
      );
    }
  }
};

export default React.memo(MenuScreenInnerBlock);
