        myObjProfile.options[index].optionsList[listIndex].selected == true
          ? {
              backgroundColor: "rgba(205,213,255,1)",
              borderWidth: 2,
              borderColor: "rgba(205,213,255,1)",
            }
          : {
              borderWidth: 2,
              borderColor: "rgba(203,202,202,1)",
            },
      ]}
    >
      {title}
    </TouchableOpacity>
  );
};

const ProductOption1Selectable = ({
  label,
  options,
  setValue,
  value,
  style,
}) => {
  return (
    <View style={{ flexWrap: "wrap", flexDirection: "row" }}>
      {options.map((option, listIndex) => (
        <HoverButton
          key={listIndex}
          title={`${option.label}  (+$${
            option.priceIncrease !== null ? option.priceIncrease : 0
          })`}
          onPress={() => {
            setValue({
              option: {
                label: option.label,
                priceIncrease:
                  option.priceIncrease !== null ? option.priceIncrease : 0,
              },
              listIndex: listIndex,
            });
          }}
        />
      ))}
    </View>
  );
};

export default ProductOption1Selectable;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
  },
});
