            <View
              style={{
                margin: 10,
                width: "40%",
                height: 52,
              }}
            >
              <ReactSelect
                options={ifOptionOptions}
                value={
                  ifStatement.selectedCaseKey && {
                    value: ifStatement.selectedCaseKey,
                    label: ifStatement.selectedCaseKey,
                  }
                }
                onChange={(val) => {
                  // newProductOptions.current[index].selectedCaseList[
                  //   indexOfIf
