                  }),
                  indicatorSeparator: (state) => ({
                    display: "none",
                  }),
                  indicatorsContainer: (provided, state) => ({
                    ...provided,
                    height: "52px",
                  }),
                }}
                menuPlacement="auto"
                menuPosition="fixed"
              />
            </View>
            <TouchableOpacity
              style={{
                height: 40,
                width: 40,
                borderRadius: 20,
                backgroundColor: "grey",
                justifyContent: "center",
                alignItems: "center",
              }}
              onPress={() => {
                // newProductOptions.current[index].selectedCaseList.splice(
                //   indexOfIf,
                //   1
                // );
                setnewProductOptions((prev) => {
                  const clone = structuredClone(prev);
                  clone[index].selectedCaseList.splice(indexOfIf, 1);
                  return clone;
                }
                );
                sete((prev) => ({
                  ...prev,
                  selectedCaseList:
                    newProductOptions[index].selectedCaseList,
                }));
              }}
            >
              <Feather name="x" size={30} color="white" />
            </TouchableOpacity>
          </View>
        );
      })}
      {optionLbls.length > 1 && (
        <Button
          title="Add If Statement"
          onPress={() => {
            if (!newProductOptions[index].selectedCaseList) {
              // newProductOptions.current[index].selectedCaseList = [
              //   {
              //     selectedCaseKey: null,
              //     selectedCaseValue: null,
