              // ];
              setnewProductOptions((prev) => {
                const clone = structuredClone(prev);
                clone[index].selectedCaseList = [
                  {
                    selectedCaseKey: null,
                    selectedCaseValue: null,
                  },
                ];
                return clone;
              }
              );
              sete((prev) => ({
                ...prev,
                selectedCaseList: [
                  { selectedCaseKey: null, selectedCaseValue: null },
                ],
              }));
            } else {
              newProductOptions.current[index].selectedCaseList.push({
                selectedCaseKey: null,
                selectedCaseValue: null,
              });

              sete((prev) => ({
                ...prev,
                selectedCaseList:
                  newProductOptions[index].selectedCaseList,
              }));
            }
          }}
          style={{ backgroundColor: "#4050B5", marginBottom: 25 }}
        />
