};

const OptionView = ({
  item,
  index,
  setnewProduct,
  newProduct,
  newProductOptions,
  indexOn,
  setindexOn,
}) => {
  const [e, sete] = useState(structuredClone(item));
  // const [testMap, settestMap] = useState(structuredClone(item.optionsList));
  // var optionLbls = newProduct.options.map(function (el) {
  //   if (el.label !== e.label && el.label !== null) {
  //     return el.label;
  //   }
  // });

  // if (e.selectedCaseKey || e.selectedCaseValue) {
  //   newProductOptions.current[index].selectedCaseList = [
  //     {
  //       selectedCaseKey: e.selectedCaseKey,
  //       selectedCaseValue: e.selectedCaseValue,
  //     },
  //   ];
  //   newProductOptions.current[index].selectedCaseKey = null;
  //   newProductOptions.current[index].selectedCaseValue = null;
  //   sete((prev) => ({
  //     ...prev,
  //     selectedCaseList: [{ selectedCaseKey: null, selectedCaseValue: null }],
  //   }));
  // }

  return (
    <View>
      <View
        style={{
          margin: 15,
          backgroundColor: "lightgrey",
          borderRadius: 10,
        }}
      >
        <TouchableOpacity
          onPress={() =>
            indexOn !== index ? setindexOn(index) : setindexOn(null)
          }
          style={[
            {
              backgroundColor: "grey",
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
              padding: 5,
              borderTopLeftRadius: 10,
              borderTopRightRadius: 10,
            },
            indexOn !== index && {
              borderBottomLeftRadius: 10,
              borderBottomRightRadius: 10,
            },
          ]}
        >
          <Text
            style={{
              width: "50%",
              paddingLeft: 5,
              fontSize: 16,
              color: "white",
              fontFamily: "archivo-600",
            }}
          >
            {item.label ? item.label : "New Option"}
          </Text>
          <TouchableOpacity
            style={{
              height: 50,
              width: 50,
              borderRadius: 25,
              backgroundColor: "grey",
              justifyContent: "center",
              alignItems: "center",
            }}
            onPress={() => {
              function moveItem(from, to) {
                // remove `from` item and store it
                const f = newProductOptions.current.splice(from, 1)[0];
                // insert stored item into position `to`
                newProductOptions.current.splice(to, 0, f);
              }

              moveItem(index, index + 1);

              setnewProduct((prevState) => ({
                ...prevState,
                options: newProductOptions.current,
              }));
              setindexOn(null);
            }}
          >
            <MaterialCommunityIcons
              name="chevron-down"
              size={32}
              color="white"
            />
          </TouchableOpacity>
          <TouchableOpacity
            style={{
              height: 50,
              width: 50,
              borderRadius: 25,
              backgroundColor: "grey",
              justifyContent: "center",
              alignItems: "center",
            }}
            onPress={() => {
              function moveItem(from, to) {
                // remove `from` item and store it
                const f = newProductOptions.current.splice(from, 1)[0];
                // insert stored item into position `to`
                newProductOptions.current.splice(to, 0, f);
              }

              moveItem(index, index - 1);

              setnewProduct((prevState) => ({
                ...prevState,
                options: newProductOptions.current,
              }));
              setindexOn(null);
            }}
          >
            <MaterialCommunityIcons name="chevron-up" size={32} color="white" />
          </TouchableOpacity>
          <TouchableOpacity
            style={{
              height: 50,
              width: 50,
              borderRadius: 25,
              backgroundColor: "grey",
              justifyContent: "center",
              alignItems: "center",
            }}
            onPress={() => {
              newProductOptions.current.push({
                ...item,
                label: item.label + " Copy",
              });
              // setnewProduct((prevState) => ({
              //   ...prevState,
              //   options: newProductOptions.current,
              // }));
              // setindexOn(null);

              //
              setnewProduct((prevState) => ({
                ...prevState,
                options: newProductOptions.current,
              }));
              setindexOn(newProductOptions.current.length - 1);
              //
            }}
          >
            <Feather name="copy" size={32} color="white" />
          </TouchableOpacity>
          <TouchableOpacity
            style={{
              height: 50,
              width: 50,
              borderRadius: 25,
              backgroundColor: "grey",
              justifyContent: "center",
              alignItems: "center",
            }}
            onPress={() => {
              const newProductOptionsUpdated = newProduct.options.filter(
                (e, filterIndex) => filterIndex !== index
              );
              newProductOptions.current.splice(index, 1);

              setnewProduct((prevState) => ({
                ...prevState,
                options: newProductOptionsUpdated,
              }));
              setindexOn(null);
            }}
          >
            <Feather name="x" size={32} color="white" />
          </TouchableOpacity>
        </TouchableOpacity>
        {indexOn === index && (
          <View style={{ padding: 20 }}>
            <InnerOn
              item={item}
              index={index}
              newProduct={newProduct}
              newProductOptions={newProductOptions}
              e={e}
              sete={sete}
            />
          </View>
        )}
      </View>
      {newProduct.options.length - 1 === index && (
        <Button
          title="Add Option"
          onPress={() => {
            newProductOptions.current.push({
              label: null,
              optionsList: [],
              selectedCaseKey: null,
              selectedCaseValue: null,
              numOfSelectable: null,
              id: Math.random().toString(36).substr(2, 9),
              optionType: null,
            });
            setnewProduct((prevState) => ({
              ...prevState,
              options: newProductOptions.current,
            }));
            setindexOn(newProductOptions.current.length - 1);
          }}
          style={{ marginBottom: 25, backgroundColor: "#4050B5" }}
          disabled={e.label === null}
        />
      )}
    </View>
  );
};

export default OptionView;
