import React, { useEffect, useRef, useState } from 'react'
import { Upload } from '../../EntryFile/imagePath';
import Select2 from 'react-select2-wrapper';
import 'react-select2-wrapper/css/select2.css';
import { employeesState, setUserStoreState, userState, userStoreState } from 'state/state';
import { updateData } from 'state/firebaseFunctions';
import { FlatList, Image, Modal, TouchableOpacity, View } from 'react-native';
import OptionView from 'components/OptionView';
import { Button } from 'react-native';
import { Link, useHistory, useParams } from 'react-router-dom';
import { auth, db, storage } from 'state/firebaseConfig';

const EditEmployee = () => {
    const { employeeId } = useParams()
    const employees = employeesState.use()
    const [employee, setemployee] = useState(employees[employeeId]);

    const history = useHistory();
    const [error, seterror] = useState(false)

    function handleDataUpdate() {
        // if (!newProduct.name) {
        //     seterror('Please enter a product name')
        //     return
        // }
        // if (!newProduct.category) {
        //     seterror('Please select a category')
        //     return
        // }
        // if (!newProduct.price) {
        //     seterror('Please enter a price')
        //     return
        // }

        // if (selectedFile) {
        //     storage
        //         .ref(auth.currentUser.uid + '/images/' + newProduct.id)
        //         .put(selectedFile);

        //     newProduct.hasImage = true
        // }

        // db.collection("users")
        //     .doc(userS.uid)
        //     .collection("products")
        //     .doc(newProduct.id.toString())
        //     .set(newProduct)
        // setUserStoreState({ categories: catalog.categories, products: [...catalog.products, newProduct] })
        // history.push("/authed/product/productlist-product");
    }

    return (
        <>
            <div className="page-wrapper">
                <div className="content">
                    <div className="page-header">
                        <div className="page-title">
                            <h4>Employee Edit</h4>
                            <h6>Edit your employee details</h6>
                        </div>
                    </div>
                    {/* /add */}
                    <div className="card">
                        <div className="card-body">
                            <div className="row">
                                <div className="col-lg-3 col-sm-6 col-12">
                                    <div className="form-group">
                                        <label>Employee Name</label>
                                        <input type="text" placeholder="Product Name" value={employee.name} onChange={(event) => setemployee((prevState) => ({
                                            ...prevState,
                                            name: event.target.value,
                                        }))} />
                                    </div>
                                </div>
                                <div className="col-lg-12">
                                    <button className="btn btn-submit me-2" onClick={handleDataUpdate}>
                                        Add
                                    </button>
                                    <Link style={{ textDecoration: 'none' }} to="/authed/product/productlist-product" className="btn btn-cancel">
                                        Cancel
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </div>
                    <Modal visible={error} transparent={true}>
                        <TouchableOpacity
                            onPress={() => seterror(false)}
                            style={{
                                height: "100%",
                                width: "100%",
                                justifyContent: "center",
                                alignItems: "center",
                                padding: "20%",
                                backgroundColor: "rgba(0,0,0,0.5)",
                            }}
                        >
                            <div
                                data-wf-user-form-error="true"
                                className=" error-message "
                            >
                                <div className="user-form-error-msg">
                                    {error}
                                </div>
                            </div>
                        </TouchableOpacity>
                    </Modal>
                    {/* /add */}
                </div>
            </div>
        </>
    )
}
export default EditEmployee;
