              const formatedData = ReceiptPrint(element, storeDetails);
              data = data.concat(formatedData);

              const qz = require("qz-tray");
              qz.websocket
                .connect()
                .then(function () {
                  let config = qz.configs.create(storeDetails.comSelected);
                  return qz.print(config, data);
