import { StyleSheet, Text, TouchableOpacity, View } from "react-native";
import React, { useState } from "react";
import Icon from "@expo/vector-icons/MaterialCommunityIcons";

const HoverButton = ({ title, onPress }) => {
  const [hover, sethover] = useState(false);

  return (
    <button
      onClick={onPress}
      style={{
        width: "100%",
        backgroundColor: hover ? "lightblue" : "white",
        height: 40,
        textAlign: "left",
        paddingLeft: 14,
      }}
      onMouseEnter={() => sethover(true)}
      onMouseLeave={() => sethover(false)}
    >
      {title}
    </button>
  );
};

const ProductOption1Selectable = ({
  label,
  options,
  setValue,
  value,
  style,
}) => {
  return (
    <View>
      {options.map((option, listIndex) => (
        <HoverButton
          key={listIndex}
          title={`${option.label}  (+$${
            option.priceIncrease !== null ? option.priceIncrease : 0
          })`}
          onPress={() => {
            setValue({
              option: {
                label: option.label,
                priceIncrease:
                  option.priceIncrease !== null ? option.priceIncrease : 0,
              },
              listIndex: listIndex,
            });
          }}
        />
      ))}
    </View>
  );
};

export default ProductOption1Selectable;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
  },
});
