import {
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from "react-native";
import React from "react";
import MaterialCommunityIcons from "@expo/vector-icons/MaterialCommunityIcons";
import useWindowDimensions from "./useWindowDimensions";

const DiscountModal = ({ setdiscountModal }) => {
    const { height, width } = useWindowDimensions();
    const [discountAmount, setdiscountAmount] = useState(second)

  return (
    <>
      <TouchableOpacity
        onPress={() => setdiscountModal(false)}
        style={{
          backgroundColor: "rgba(0, 0, 0, 0.3)",
          justifyContent: "center",
          alignItems: "center",
          height: height,
          width: width,
        }}
      />
      <View
        style={{
          position: "absolute",
          backgroundColor: "rgba(255,255,255,1)",
          borderRadius: 30,
          shadowColor: "rgba(0,0,0,1)",
          shadowOffset: {
            width: 3,
            height: 3,
          },
          elevation: 30,
          shadowOpacity: 0.57,
          shadowRadius: 10,
          height: height * 0.7,
          width: height * 0.7,
          padding: 20,
          alignSelf: "center",
          top: "15%",
        }}
      >
        <View>
          <View
            style={{
              flexDirection: "row",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons
              onPress={() => setdiscountModal(false)}
              name="close"
              size={26}
              color="rgba(74,74,74,1)"
              style={{ width: "25%", marginLeft: 20 }}
            />
            <Text
              style={{
                margin: 25,
                fontSize: 20,
                fontWeight: "600",
                width: "50%",
                textAlign: "center",
              }}
            >
              Apply Discount
            </Text>
            <View style={{ width: "25%" }} />
            <TextInput
              placeholder="Enter Custom Amount or Percentage"
              style={{
                height: 40,
                borderColor: "gray",
                borderWidth: 1,
                borderRadius: 10,
                width: "100%",
                padding: 10,
                margin: 10,
              }}
            />
            <View
              style={{
                flexDirection: "row",
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              <TouchableOpacity
                style={{
                  backgroundColor: "grey",
                  borderRadius: 10,
                  padding: 10,
                }}
              >
                <Text>5%</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{
                  backgroundColor: "grey",
                  borderRadius: 10,
                  padding: 10,
                }}
              >
                <Text>10%</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{
                  backgroundColor: "grey",
                  borderRadius: 10,
                  padding: 10,
                }}
              >
                <Text>15%</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{
                  backgroundColor: "grey",
                  borderRadius: 10,
                  padding: 10,
                }}
              >
                <Text>20%</Text>
              </TouchableOpacity>
            </View>
            <TextInput
              placeholder="Enter Code"
              style={{
                height: 40,
                borderColor: "gray",
                borderWidth: 1,
                borderRadius: 10,
                width: "100%",
                padding: 10,
                margin: 10,
              }}
            />
            <TouchableOpacity
              style={{
                backgroundColor: "rgba(0, 0, 0, 0.1)",
                borderRadius: 10,
                padding: 10,
                margin: 10,
              }}
            >
              <Text style={{ textAlign: "center" }}>Apply</Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </>
  );
};

export default DiscountModal;

const styles = StyleSheet.create({});
