import React, { Component, useState } from "react";
import {
  StyleSheet,
  View,
  Text,
  TextInput,
  TouchableOpacity,
} from "react-native";
import { Entypo, MaterialCommunityIcons } from "@expo/vector-icons";

function OptionSelectionItem({
  style,
  eInnerListStart,
  indexInnerList,
  testMap,
  settestMap,
  newProductOptions,
  setnewProductOptions,
  index,
}) {
  const eInnerList = structuredClone(eInnerListStart);
  const [countsAsValueBeforeBlur, setcountsAsValueBeforeBlur] = useState(
    eInnerList?.countsAs ? eInnerList.countsAs : ""
  );
  const [priceIncreaseBeforeBlur, setpriceIncreaseBeforeBlur] = useState(
    eInnerList?.priceIncrease ? eInnerList.priceIncrease : ""
  );
  const [labelBeforeBlur, setlabelBeforeBlur] = useState(
    eInnerList?.label ? eInnerList.label : ""
  );

  return (
    <View style={[styles.container, style]}>
      <View
        style={
          newProductOptions[index]?.numOfSelectable > 0
            ? styles.optionSelectionNameInputGroupLimit
            : styles.optionSelectionNameInputGroup
        }
      >
        <Text style={styles.selectionNameInputLbl}>Option Selection Name</Text>
        <TextInput
          style={styles.selectionNameInput}
          // onBlur={() => {
          //   const cloneOuter = structuredClone(testMap);
          //   cloneOuter[indexInnerList].label = labelBeforeBlur;
          //   // newProductOptions.current[index].optionsList = clone;
          //   setnewProductOptions((prev) => {
          //     const clone = structuredClone(prev);
          //     clone[index].optionsList = cloneOuter;
          //     return clone;
          //   });
          //   settestMap(cloneOuter);
          // }}
          onChangeText={(val) => {
            // setlabelBeforeBlur(val);
            const cloneOuter = structuredClone(testMap);
            cloneOuter[indexInnerList].label = val;
            // newProductOptions.current[index].optionsList = clone;
            setnewProductOptions((prev) => {
              const clone = structuredClone(prev);
              clone[index].optionsList = cloneOuter;
              return clone;
            });
            settestMap(cloneOuter);
          }}
          value={labelBeforeBlur}
          placeholder="Enter Name (Ex: Small, Pepperoni, Extra Cheese) "
        />
      </View>
      <View
        style={
          newProductOptions[index]?.numOfSelectable > 0
            ? styles.selectionPriceIncreaseInputGroupLimit
            : styles.selectionPriceIncreaseInputGroup
        }
      >
        <Text style={styles.selectionPriceIncreaseLbl}>Price Increase</Text>
        <TextInput
          style={styles.selectionPriceIncreaseInput}
          onBlur={() => {
            console.log("Blurred");
            const cloneOuter = structuredClone(testMap);
            cloneOuter[indexInnerList].priceIncrease = priceIncreaseBeforeBlur;
            setnewProductOptions((prev) => {
              const clone = structuredClone(prev);
              clone[index].optionsList = cloneOuter;
              return clone;
            });
            settestMap(cloneOuter);
          }}
          onChangeText={(val) => {
            const re = /^-?\d*\.?\d*$/;

            // if value is not blank, then test the regex

            if (val === "" || re.test(val)) {
              setpriceIncreaseBeforeBlur(val);
            }
          }}
          value={priceIncreaseBeforeBlur}
          placeholder="Enter If Price Increases With Selection"
        />
      </View>
      {newProductOptions[index].numOfSelectable > 0 && (
        <View style={styles.selectionLimitInputGroupLimit}>
          <Text style={styles.limit}>Limit</Text>
          <View style={styles.selectionLimitInput}></View>
        </View>
      )}
      <View style={styles.btnsRow}>
        <TouchableOpacity
          style={styles.moveDownBtn}
          onPress={() => {
            if (testMap.length > 1 && indexInnerList !== testMap.length - 1) {
              setnewProductOptions((prev) => {
                const clone = structuredClone(prev);
                const f = clone[index].optionsList.splice(indexInnerList, 1)[0];
                clone[index].optionsList.splice(indexInnerList + 1, 0, f);
                settestMap(clone[index].optionsList);
                return clone;
              });
            }
          }}
        >
          <Entypo name="chevron-down" style={styles.chevronDown} />
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.moveUpBtn}
          onPress={() => {
            if (testMap.length > 1 && indexInnerList !== 0) {
              setnewProductOptions((prev) => {
                const clone = structuredClone(prev);
                const f = clone[index].optionsList.splice(indexInnerList, 1)[0];
                clone[index].optionsList.splice(indexInnerList - 1, 0, f);
                settestMap(clone[index].optionsList);
                return clone;
              });
            }
          }}
        >
          <Entypo name="chevron-up" style={styles.chevronUp} />
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.deleteBtn}
          onPress={() => {
            const cloneOuter = structuredClone(testMap);
            cloneOuter.splice(indexInnerList, 1);
            // newProductOptions.current[index].optionsList = clone;
            setnewProductOptions((prev) => {
              const clone = structuredClone(prev);
              clone[index].optionsList = cloneOuter;
              return clone;
            });
            settestMap(cloneOuter);
          }}
        >
          <MaterialCommunityIcons
            name="delete-outline"
            style={styles.deleteIcon}
          />
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "flex-end",
  },
  optionSelectionNameInputGroup: {
    width: 351,
    height: 84,
    justifyContent: "space-between",
  },
  selectionNameInputLbl: {
    color: "#121212",
    fontSize: 17,
  },
  selectionNameInput: {
    width: "100%",
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5,
    padding: 10,
  },
  selectionPriceIncreaseInputGroup: {
    width: 199,
    height: 84,
    justifyContent: "space-between",
  },
  selectionPriceIncreaseLbl: {
    color: "#121212",
    fontSize: 17,
  },
  selectionPriceIncreaseInput: {
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5,
    alignSelf: "stretch",
    padding: 10,
  },
  btnsRow: {
    width: 197,
    height: 50,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginRight: 0,
    marginLeft: 0,
  },
  moveUpBtn: {
    width: 50,
    height: 50,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  chevronUp: {
    color: "rgba(255,255,255,1)",
    fontSize: 30,
  },
  moveDownBtn: {
    width: 50,
    height: 50,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  chevronDown: {
    color: "rgba(255,255,255,1)",
    fontSize: 30,
  },
  deleteBtn: {
    width: 50,
    height: 50,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  deleteIcon: {
    color: "rgba(255,255,255,1)",
    fontSize: 27,
  },
  optionSelectionNameInputGroupLimit: {
    width: 280,
    height: 84,
    justifyContent: "space-between",
  },
  selectionPriceIncreaseInputGroupLimit: {
    width: 140,
    height: 84,
    justifyContent: "space-between",
  },
  selectionLimitInputGroupLimit: {
    width: 120,
    height: 84,
    justifyContent: "space-between",
  },
  limit: {
    color: "#121212",
    fontSize: 17,
  },
  selectionLimitInput: {
    height: 50,
    backgroundColor: "#ffffff",
    borderWidth: 1,
    borderColor: "#9b9b9b",
    borderRadius: 5,
    alignSelf: "stretch",
  },
});

export default OptionSelectionItem;
