import { View, Text, StyleSheet, TouchableOpacity } from "react-native";
import React from "react";
import { MaterialIcons } from "@expo/vector-icons";

const OrderItem = ({ key, prevOrder, prevOrderIndex }) => {
  return (
    <View style={styles.orderContainer}>
      <View style={styles.topOrderRow}>
        <Text style={styles.orderNumber}>Order #1</Text>
        <View style={styles.deliveryOrPickupRow}>
          <TouchableOpacity
            style={styles.pickupBtn}
            onPress={() => {
              setCartState(prevOrder.cart);
              setOngoingDelivery(true);
              setNameForDelivery(customerSelected.name);
              setPhoneForDelivery(customerSelected.phone);
              setAddressForDelivery(customerSelected.address);
              setBuzzCodeForDelivery(customerSelected.buzzCode);
              setUnitNumberForDelivery(customerSelected.unitNumber);
              setDeliveryChecked(false);
              setSaveCustomerModal(false);
            }}
          >
            <MaterialIcons
              name="store-mall-directory"
              style={styles.pickupIcon}
            />
          </TouchableOpacity>
          <TouchableOpacity style={styles.deliveryBtn}>
            <MaterialIcons name="directions-car" style={styles.deliveryIcon} />
          </TouchableOpacity>
        </View>
      </View>
      <View style={styles.itemContainer}>
        <Text style={styles.itemName}>Creamy Ranch Dip</Text>
        <Text style={styles.itemPrice}>$1.50</Text>
        <Text style={styles.itemQty}>Qty: 1</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  orderContainer: {
    width: 439,
    alignItems: "center",
    justifyContent: "space-between",
    marginBottom: 30,
    borderBottomWidth: 1,
    paddingBottom: 20,
  },
  topOrderRow: {
    width: 439,
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 15,
  },
  orderNumber: {
    fontWeight: "700",
    color: "#121212",
    fontSize: 15,
  },
  deliveryOrPickupRow: {
    width: 101,
    height: 40,
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
  },
  pickupBtn: {
    width: 35,
    height: 35,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  pickupIcon: {
    color: "rgba(255,255,255,1)",
    fontSize: 30,
  },
  deliveryBtn: {
    width: 35,
    height: 35,
    backgroundColor: "#1c294e",
    borderRadius: 10,
    alignItems: "center",
    justifyContent: "center",
  },
  deliveryIcon: {
    color: "rgba(255,255,255,1)",
    fontSize: 30,
  },
  itemContainer: {
    width: 439,
    height: 53,
    backgroundColor: "#edf1fe",
    borderRadius: 10,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  itemName: {
    color: "#121212",
    fontSize: 15,
    marginLeft: 15,
  },
  itemPrice: {
    color: "#03c551",
    fontSize: 15,
  },
  itemQty: {
    color: "#121212",
    fontSize: 15,
    marginRight: 15,
  },
});

export default OrderItem;
