import React, { useState } from "react";

type GeneralSwitchProps = {
  isActive: boolean;
  setIsActive: React.Dispatch<React.SetStateAction<boolean>>;
  customToggleFunctionOn?: () => void;
  customToggleFunctionOff?: () => void;
};

const GeneralSwitch = ({
  isActive,
  setIsActive,
  customToggleFunctionOn,
  customToggleFunctionOff,
}: GeneralSwitchProps) => {
  // Function to toggle switch state
  const toggleSwitch = () => {
    if (setIsActive) {
      setIsActive(!isActive);
    }

    if (isActive) {
      if (customToggleFunctionOff) {
        customToggleFunctionOff();
      }
    } else {
      if (customToggleFunctionOn) {
        customToggleFunctionOn();
      }
    }
  };

  // Styles
  const switchStyles = {
    cursor: "pointer",
    width: "39px",
    height: "18px",
    borderRadius: "15px",
    backgroundColor: isActive ? "#1D294E" : "#ccc",
    position: "relative",
    transition: "background-color 0.3s",
  };

  const knobStyles = {
    position: "absolute",
    top: "0px",
    left: isActive ? "21px" : "2px", // Move knob based on isActive
    width: "18px",
    height: "18px",
    borderRadius: "50%",
    backgroundColor: "#fff",
    transition: "left 0.3s",
  };

  return (
    <div style={switchStyles} onClick={toggleSwitch}>
      <div style={knobStyles}></div>
    </div>
  );
};

export default GeneralSwitch;
