import { Image, StyleSheet, Text, View } from "react-native";
import React, { useEffect, useState } from "react";
import MenuScreen from "./MenuScreen";
import CartScreen from "./CartScreen";
import { Button } from "@react-native-material/core";
import { sectionState, userStoreState } from "state/state";
import { auth } from "state/firebaseConfig";
import Logo from "assets/dpos-logo.png";

const HomeScreen = ({ navigation }) => {
  const catalog = userStoreState.use();
  const section = sectionState.use();

  const Header = () => {

    return (
      <View
        style={{
          width: "100%",
          height: 100,
          flexDirection: "row",
          alignItems: "center",
          justifyContent: "space-between",
          backgroundColor: "red",
        }}
      >
        <Image
          source={Logo}
          style={{ width: 250, height: 160, resizeMode: "contain" }}
        />
        <Button
          titleStyle={{ color: "black", fontWeight: "700" }}
          style={{ backgroundColor: "white" }}
          onPress={() => navigation.navigate("SettingsHome")}
          title="Settings"
        />
      </View>
    );
  };

  return (
    <View style={{ flexDirection: "row", height: "100%" }}>
      <View>
        <Header />
        <MenuScreen catalog={catalog} section={section} navigation={navigation} />
        </View>
      <CartScreen />
    </View>
  );
};

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    backgroundColor: "white",
    flex: 1,
  },
});
