import { View, Text, Button, StyleSheet, TouchableOpacity } from "react-native";
import React, { useState } from "react";
import Entypo from "@expo/vector-icons/Entypo";
import Feather from "@expo/vector-icons/Feather";
import { userStoreState } from "state/state";

const ProductAndCatManager = ({ navigation }) => {
  const catalog = userStoreState.use();
  const [selectedCategory, setselectedCategory] = useState(null);

  return (
    <View style={{ justifyContent: "center", alignItems: "center" }}>
      <Text>ProductAndCatManager</Text>
      <Button
        title="Edit Products"
        onPress={() => navigation.navigate("Edit ProductList")}
      />
      <Button
        title="Edit Categories"
        onPress={() => navigation.navigate("Edit Categories")}
      />
      <View style={styles.container}>
        <View style={styles.left}>
          <View style={styles.headerContainer}>
            <Text style={styles.headerLbl}>Categories</Text>
          </View>
          <View style={styles.bottomContainer}>
            <View style={styles.addNewContainer}>
              <Entypo name="plus" style={styles.addNewIcon} />
              <Text style={styles.addNewTxt}>Add new category</Text>
            </View>
            {catalog.categories.map((e, index) => {
              return (
                <TouchableOpacity
                  onPress={() => setselectedCategory(e)}
                  style={[
                    styles.selectedItemContainer,
                    e === selectedCategory && { backgroundColor: "#ebebeb" },
                  ]}
                >
                  <Text style={styles.selectedItemTxt}>{e}</Text>
                  <Feather
                    name="more-vertical"
                    style={styles.selectedItemIcon}
                  />
                </TouchableOpacity>
              );
            })}
          </View>
        </View>
        <View style={styles.left}>
          <View style={styles.headerContainer}>
            <Text style={styles.headerLbl}>Products</Text>
          </View>
          <View style={styles.bottomContainer}>
            <View style={styles.addNewContainer}>
              <Entypo name="plus" style={styles.addNewIcon} />
              <Text style={styles.addNewTxt}>Add new product</Text>
            </View>
            <View
              style={[
                styles.selectedItemContainer,
                false && { backgroundColor: "#ebebeb" },
              ]}
            >
              <Text style={styles.selectedItemTxt}>Pizzas</Text>
              <Feather name="more-vertical" style={styles.selectedItemIcon} />
            </View>
            <View
              style={[
                styles.selectedItemContainer,
                true && { backgroundColor: "#ebebeb" },
              ]}
            >
              <Text style={styles.selectedItemTxt}>Pizzas</Text>
              <Feather name="more-vertical" style={styles.selectedItemIcon} />
            </View>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    borderWidth: 1,
    borderColor: "#dcdcdc",
    flexDirection: "row",
    width: 609,
    height: 491,
  },
  left: {
    width: "50%",
    height: "100%",
    borderWidth: 1,
    borderColor: "#dcdcdc",
  },
  headerContainer: {
    width: "100%",
    height: 40,
    borderWidth: 0,
    borderColor: "#dcdcdc",
    borderBottomWidth: 1,
    backgroundColor: "#fafafa",
  },
  headerLbl: {
    fontFamily: "archivo-500",
    color: "#717171",
    marginTop: 14,
    marginLeft: 43,
  },
  bottomContainer: {
    width: "100%",
  },
  addNewContainer: {
    width: "100%",
    height: 38,
    flexDirection: "row",
    alignItems: "center",
  },
  addNewIcon: {
    color: "#2076e9",
    fontSize: 30,
    marginLeft: 10,
  },
  addNewTxt: {
    fontFamily: "archivo-500",
    color: "#1b73e8",
    marginLeft: 10,
  },
  selectedItemContainer: {
    height: 33,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
    paddingLeft: 40,
    paddingRight: 10,
  },
  selectedItemTxt: {
    fontFamily: "archivo-500",
    color: "#252525",
  },
  selectedItemIcon: {
    color: "#6a6a6a",
    fontSize: 20,
  },
});

export default ProductAndCatManager;
